SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE eis_gerar_cadastro (nm_usuario_p text) AS $body$
DECLARE



nr_sequencia_w		bigint;
nm_tabela_w			varchar(50);
qt_registro_w			bigint;
qt_reg_ant_w			bigint;
qt_incremento_w		bigint;
dt_referencia_w		timestamp;
ds_comando_w			varchar(2000);

C01 CURSOR FOR
SELECT	nm_tabela
from	tabela_sistema
where	nm_tabela in (
	'PESSOA_FISICA',
	'PESSOA_JURIDICA',
	'MATERIAL',
	'INTERVALO_PRESCRICAO',
	'EXAME_LAB_ROTINA',
	'EXAME_LABORATORIO',
	'PROCEDIMENTO_ROTINA',
	'MATERIAL_DILUICAO',
	'MATERIAL_CONVERSAO_UNIDADE',
	'UNIDADE_MEDIDA');



BEGIN

dt_referencia_w		:= trunc(clock_timestamp(),'dd');
delete from eis_cadastro
where	dt_referencia		= dt_referencia_w;

OPEN C01;
LOOP
FETCH C01 into
	nm_tabela_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	select	nextval('eis_cadastro_seq')
	into STRICT	nr_sequencia_w
	;
	qt_registro_w		:= 0;
	ds_comando_w := 'select count(*) from ' || nm_tabela_w;
	qt_registro_w := Obter_Valor_Dinamico(ds_comando_w, qt_registro_w);

	select	coalesce(max(qt_registro),0)
	into STRICT	qt_reg_ant_w
	from	eis_cadastro
	where	nm_tabela	= nm_tabela_w
	and	dt_referencia	= (
		SELECT max(dt_referencia)
		from	eis_cadastro
		where	nm_tabela	= nm_tabela_w
		and	dt_referencia	between dt_referencia_w - 30 and dt_referencia_w);

	qt_incremento_w	:= qt_registro_w - qt_reg_ant_w;

	insert into eis_cadastro(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_referencia,
		nm_tabela,
		qt_registro,
		qt_incremento)
	values (
		nr_sequencia_w,
		clock_timestamp(),
		nm_usuario_p,
		dt_referencia_w,
		nm_tabela_w,
		qt_registro_w,
		qt_incremento_w);
END LOOP;
CLOSE C01;

commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
