CREATE OR REPLACE PROCEDURE eis_gerar_indic_clinico ( dt_referencia_p timestamp, nm_usuario_p text) AS $body$
DECLARE

 
 
nr_sequencia_w		bigint;
qt_incremento_w		bigint;
dt_referencia_w		timestamp;
cd_estabelecimento_w		bigint;
nr_seq_indicador_w		bigint;
qt_total_w			bigint;
qt_obito_w			bigint;
cd_medico_w			varchar(10);

C01 CURSOR FOR 
SELECT	a.cd_estabelecimento, 
	b.nr_seq_indicador, 
	b.cd_medico, 
	count(*), 
	sum(CASE WHEN b.ie_obito='S' THEN 1  ELSE 0 END ) 
from	Atend_indic_clinico b, 
	atendimento_paciente a 
where	a.nr_atendimento		= b.nr_atendimento 
and	a.dt_alta between dt_referencia_w and last_day(dt_referencia_w) + 86399/86400 
group by 
	a.cd_estabelecimento, 
	b.nr_seq_indicador, 
	b.cd_medico;
	

BEGIN 
 
dt_referencia_w		:= trunc(dt_referencia_p,'month');
delete from eis_indic_clinico 
where	dt_referencia		= dt_referencia_w;
 
OPEN C01;
LOOP 
FETCH C01 into 
	cd_estabelecimento_w, 
	nr_seq_indicador_w, 
	cd_medico_w, 
	qt_total_w, 
	qt_obito_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	select	nextval('eis_indic_clinico_seq') 
	into STRICT	nr_sequencia_w 
	;
	insert into eis_indic_clinico( 
		nr_sequencia, 
		cd_estabelecimento, 
		dt_referencia, 
		dt_atualizacao, 
		nm_usuario, 
		nr_seq_indicador, 
		qt_total, 
		qt_obito, 
		cd_medico) 
	values ( 
		nr_sequencia_w, 
		cd_estabelecimento_w, 
		dt_referencia_w, 
		clock_timestamp(), 
		nm_usuario_p, 
		nr_seq_indicador_w, 
		qt_total_w, 
		qt_obito_w, 
		cd_medico_w);
END LOOP;
CLOSE C01;
commit;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
