CREATE OR REPLACE PROCEDURE eis_gerar_prescr_mensal (dt_inicio_mes_p timestamp, dt_final_mes_p timestamp, nm_usuario_p text) AS $body$
DECLARE

 
dt_atual_w	timestamp;
dt_final_w	timestamp;


BEGIN 
 
dt_atual_w	:= trunc(dt_inicio_mes_p,'dd');
dt_final_w	:= trunc(dt_final_mes_p,'dd');
 
while(dt_atual_w <= dt_final_w)loop 
	begin 
	if (dt_atual_w < dt_final_w) then 
		CALL gerar_eis_prescricao(dt_atual_w, nm_usuario_p, 'N');
	else 
		CALL gerar_eis_prescricao(dt_atual_w, nm_usuario_p, 'S');
	end if;
	dt_atual_w	:= dt_atual_w + 1;
	end;
end loop;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
