SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE eis_gerar_w_result ( dt_parametro_p timestamp, cd_empresa_p bigint, ie_nota_fiscal_p text) AS $body$
DECLARE

 
dt_referencia_w			timestamp;
dt_referencia_ww			timestamp;
cd_conta_contabil_w		varchar(20);
cd_convenio_w			bigint;
cd_setor_atendimento_w		bigint;
vl_faturado_w			double precision;
vl_producao_w			double precision;
vl_fat_prod_ant_w			double precision;
vl_fat_prod_mes_w			double precision;
vl_fat_prod_mes_ant_w		double precision;
vl_pre_fatur_w			double precision;
vl_pre_fatur_fat_mes_w		double precision;
vl_pre_fatur_ant_w			double precision;
vl_receita_antecipada_w		double precision;
qt_reg_w				bigint;
vl_prod_atual_fat_mes_ant_w		double precision;
ie_nota_fiscal_w		varchar(1);

C01 CURSOR FOR 
/*	Faturamento */
 
	SELECT	cd_convenio,	 
		cd_setor_atendimento, 
		cd_conta_contabil, 
		sum(vl_total), 
		0,0,0,0,0,0,0,0,0 
	from	eis_resultado_v 
	where	dt_referencia	between dt_referencia_w and dt_referencia_ww 
	and	((ie_nota_fiscal_w = 'T') or (obter_se_conta_protocolo_nf(nr_interno_conta,nr_seq_protocolo) = ie_nota_fiscal_w)) 
	and	cd_estabelecimento	in ( 
		SELECT	cd_estabelecimento 
		from	estabelecimento 
		where	cd_empresa	= cd_empresa_p) 
	group by 
		cd_convenio,	 
		cd_setor_atendimento, 
		cd_conta_contabil 
	
union all
 
/*	Produção */
 
	Select	cd_convenio,	 
		cd_setor_atendimento, 
		cd_conta_contabil, 
		0, 
		sum(vl_total), 
		0,0,0,0,0,0,0,0 
	from	eis_resultado_v 
	where	dt_receita between dt_referencia_w and dt_referencia_ww 
	and	cd_estabelecimento	in ( 
		select	cd_estabelecimento 
		from	estabelecimento 
		where	cd_empresa	= cd_empresa_p) 
	group by 
		cd_convenio,	 
		cd_setor_atendimento, 
		cd_conta_contabil 
	
union all
 
/*	Faturamento da produção anterior*/
 
	Select	cd_convenio,	 
		cd_setor_atendimento, 
		cd_conta_contabil, 
		0,0, 
		sum(vl_total), 
		0,0,0,0,0,0,0 
	from	eis_resultado_v 
	where	dt_referencia = dt_referencia_w 
	and	dt_receita < dt_referencia_w 
	and	cd_estabelecimento	in ( 
		select	cd_estabelecimento 
		from	estabelecimento 
		where	cd_empresa	= cd_empresa_p) 
	group by 
		cd_convenio,	 
		cd_setor_atendimento, 
		cd_conta_contabil 
	
union all
 
/*	Faturamento da produção do mês */
 
	Select	cd_convenio,	 
		cd_setor_atendimento, 
		cd_conta_contabil, 
		0,0,0,0, 
		sum(vl_total), 
		0,0,0,0,0 
	from	eis_resultado_v 
	where	dt_referencia between dt_referencia_w and dt_referencia_ww 
	and	dt_receita between dt_referencia_w and dt_referencia_ww	 
	and	cd_estabelecimento	in ( 
		select	cd_estabelecimento 
		from	estabelecimento 
		where	cd_empresa	= cd_empresa_p) 
	group by 
		cd_convenio,	 
		cd_setor_atendimento, 
		cd_conta_contabil 
	
union all
 
/*	Faturamento da produção do mês anterior */
 
	Select	cd_convenio,	 
		cd_setor_atendimento, 
		cd_conta_contabil, 
		0,0,0, 
		0,0, 
		sum(vl_total),0,0,0,0 
	from	eis_resultado_v 
	where	dt_referencia between dt_referencia_w and dt_referencia_ww 
	and	dt_receita between pkg_date_utils.add_month(dt_referencia_w,-1,0) and pkg_date_utils.end_of(pkg_date_utils.add_month(dt_referencia_ww,-1,0), 'MONTH',0) 
	and	cd_estabelecimento	in ( 
		select	cd_estabelecimento 
		from	estabelecimento 
		where	cd_empresa	= cd_empresa_p) 
	group by 
		cd_convenio,	 
		cd_setor_atendimento, 
		cd_conta_contabil 
	
union all
 
/*	Pre-faturamento */
 
	Select	cd_convenio,	 
		cd_setor_atendimento, 
		cd_conta_contabil, 
		0, 
		0, 
		0, 
		sum(vl_material + vl_procedimento), 
		0,0,0,0,0,0 
	from	pre_faturamento 
	where	dt_referencia between dt_referencia_w and dt_referencia_ww 
	and	cd_estabelecimento	in ( 
		select	cd_estabelecimento 
		from	estabelecimento 
		where	cd_empresa	= cd_empresa_p) 
	group by 
		cd_convenio,	 
		cd_setor_atendimento, 
		cd_conta_contabil 
	
union all
 
/*	Receita antecipada */
 
	Select	cd_convenio,	 
		cd_setor_atendimento, 
		cd_conta_contabil, 
		0,0,0,0, 
		0, 
		0, 
		sum(vl_total), 
		0, 
		0,0 
	from	eis_resultado_v 
	where	dt_referencia between dt_referencia_w and dt_referencia_ww 
	and	dt_receita > dt_referencia_ww 
	and	cd_estabelecimento	in ( 
		select	cd_estabelecimento 
		from	estabelecimento 
		where	cd_empresa	= cd_empresa_p) 
	group by 
		cd_convenio,	 
		cd_setor_atendimento, 
		cd_conta_contabil 
	
union all
 
/*	Pre-faturamento mêses anteriores */
 
	select	b.cd_convenio,	 
		b.cd_setor_atendimento, 
		b.cd_conta_contabil, 
		0, 
		0, 
		0, 
		0, 
		0, 
		0, 
		0, 
		sum(b.vl_material + b.vl_procedimento), 
		0,0 
	from	pre_faturamento b 
	where	b.dt_referencia	= dt_referencia_w 
	and	b.dt_receita	<> dt_referencia_w 
	and	b.cd_estabelecimento	in ( 
		select	y.cd_estabelecimento 
		from	estabelecimento y 
		where	y.cd_empresa	= cd_empresa_p) 
	group by 
		b.cd_convenio,	 
		b.cd_setor_atendimento, 
		b.cd_conta_contabil 
	
union all
 
	/* Pré-faturamento mês*/
 
	select	b.cd_convenio,	 
		b.cd_setor_atendimento, 
		b.cd_conta_contabil, 
		0, 
		0, 
		0, 
		0, 
		0, 
		0, 
		0, 
		0, 
		sum(b.vl_material + b.vl_procedimento),0 
	from	pre_faturamento b 
	where	b.dt_referencia	= b.dt_receita 
	and	b.dt_referencia	= dt_referencia_w 
	and	b.cd_estabelecimento	in ( 
		select	y.cd_estabelecimento 
		from	estabelecimento y 
		where	y.cd_empresa	= cd_empresa_p) 
	group by 
		b.cd_convenio,	 
		b.cd_setor_atendimento, 
		b.cd_conta_contabil 
	
union all
 
	/*Produção do mês faturada em outros meses*/
 
	Select	cd_convenio,	 
		cd_setor_atendimento, 
		cd_conta_contabil, 
		0,0, 
		0, 
		0,0,0,0,0,0,sum(vl_total) 
	from	eis_resultado_v 
	where	dt_referencia	< dt_referencia_w 
	and	dt_receita 	= dt_referencia_w 
	and	cd_estabelecimento	in ( 
		select	cd_estabelecimento 
		from	estabelecimento 
		where	cd_empresa	= cd_empresa_p) 
	group by 
		cd_convenio,	 
		cd_setor_atendimento, 
		cd_conta_contabil;


BEGIN 
 
delete	from w_eis_result;
dt_referencia_w	:= pkg_date_utils.start_of(dt_parametro_p,'MONTH',0);
dt_referencia_ww	:= pkg_date_utils.end_of(dt_referencia_w, 'MONTH', 0);
 
ie_nota_fiscal_w	:= coalesce(ie_nota_fiscal_p,'T');
 
OPEN C01;
LOOP 
FETCH C01 into 
	cd_convenio_w, 
	cd_setor_atendimento_w, 
	cd_conta_contabil_w, 
	vl_faturado_w, 
	vl_producao_w, 
	vl_fat_prod_ant_w, 
	vl_pre_fatur_w, 
	vl_fat_prod_mes_w, 
	vl_fat_prod_mes_ant_w, 
	vl_receita_antecipada_w, 
	vl_pre_fatur_ant_w, 
	vl_pre_fatur_fat_mes_w, 
	vl_prod_atual_fat_mes_ant_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	select	count(*) 
	into STRICT	qt_reg_w 
	from	w_eis_result 
	where	dt_referencia		= dt_referencia_w 
	and	cd_convenio		= cd_convenio_w 
	and	cd_setor_atendimento	= cd_setor_atendimento_w 
	and	cd_conta_contabil	= cd_conta_contabil_w;
 
	if (qt_reg_w = 0) then 
		insert into w_eis_result( 
			dt_referencia, 
			cd_convenio, 
			cd_setor_atendimento, 
			cd_conta_contabil, 
			vl_faturado, 
			vl_producao, 
			vl_fat_prod_ant, 
			vl_fat_prod_mes, 
			vl_fat_prod_mes_ant, 
			vl_pre_fatur, 
			vl_receita_antecipada, 
			vl_pre_fatur_ant, 
			vl_pre_fatur_fat_mes, 
			vl_prod_fat_ant) 
		values (	dt_referencia_w, 
			cd_convenio_w, 
			cd_setor_atendimento_w, 
			cd_conta_contabil_w, 
			vl_faturado_w, 
			vl_producao_w, 
			vl_fat_prod_ant_w, 
			vl_fat_prod_mes_w, 
			vl_fat_prod_mes_ant_w, 
			vl_pre_fatur_w, 
			vl_receita_antecipada_w, 
			vl_pre_fatur_ant_w, 
			vl_pre_fatur_fat_mes_w, 
			vl_prod_atual_fat_mes_ant_w);
	else 
		update w_eis_result 
		set	vl_faturado		= vl_faturado + vl_faturado_w, 
			vl_producao		= vl_producao + vl_producao_w, 
			vl_fat_prod_ant		= vl_fat_prod_ant + vl_fat_prod_ant_w, 
			vl_pre_fatur		= vl_pre_fatur + vl_pre_fatur_w, 
			vl_fat_prod_mes		= vl_fat_prod_mes + vl_fat_prod_mes_w, 
			vl_fat_prod_mes_ant	= vl_fat_prod_mes_ant + vl_fat_prod_mes_ant_w, 
			vl_receita_antecipada 	= vl_receita_antecipada + vl_receita_antecipada_w, 
			vl_pre_fatur_ant	= vl_pre_fatur_ant + vl_pre_fatur_ant_w, 
			vl_pre_fatur_fat_mes	= vl_pre_fatur_fat_mes + vl_pre_fatur_fat_mes_w, 
			vl_prod_fat_ant		= vl_prod_fat_ant + vl_prod_atual_fat_mes_ant_w 
		where	dt_referencia		= dt_referencia_w 
		and	cd_convenio		= cd_convenio_w 
		and	cd_setor_atendimento	= cd_setor_atendimento_w 
		and	cd_conta_contabil	= cd_conta_contabil_w;
	end if;
END LOOP;
CLOSE C01;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
