CREATE OR REPLACE PROCEDURE eis_pac_internado_hora ( dt_parametro_p timestamp, nm_usuario_p text) AS $body$
DECLARE

 
dt_atual_w	timestamp;


BEGIN 
 
dt_atual_w	:= trunc(dt_parametro_p,'month');
 
while(dt_atual_w <= fim_mes(dt_parametro_p))loop 
	begin 
 
	if (dt_atual_w <= trunc(clock_timestamp(),'dd')) then 
		CALL gerar_eis_pac_internado_hora(dt_atual_w, nm_usuario_p);
	end if;
	dt_atual_w	:= dt_atual_w + 1;
	end;
end loop;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
