SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE eliminar_resultado ( nr_prescricao_p bigint, nr_seq_result_p bigint, nm_usuario_p text) AS $body$
DECLARE

			
nr_seq_diag_w	bigint;
ie_confirmado_w varchar(1);
qt_reg_w		bigint;

c01 CURSOR FOR
SELECT	b.nr_seq_diag,
		substr(Obter_se_diag_confirmado(a.nr_seq_prescr, b.nr_seq_diag), 1, 1) ie_confirmado
FROM 	pe_result_item_diag b,
		pe_prescr_item_result a,
		pe_diagnostico c
WHERE 	b.nr_seq_result = a.nr_seq_result
AND 	a.nr_seq_prescr = nr_prescricao_p
AND		a.nr_sequencia = nr_seq_result_p
AND 	c.nr_sequencia = b.nr_seq_diag
AND 	c.ie_situacao = 'A'
AND 	Obter_Se_Diag_Sae_Lib(nr_prescricao_p, b.nr_seq_diag) = 'S'
AND 	coalesce(c.cd_estabelecimento, coalesce(wheb_usuario_pck.get_cd_estabelecimento, 0)) = coalesce(wheb_usuario_pck.get_cd_estabelecimento, 0);



BEGIN

open c01;
loop
fetch c01 into	
	nr_seq_diag_w,
	ie_confirmado_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	
	SELECT	count(*)
	into STRICT	qt_reg_w
	FROM 	pe_result_item_diag b,
			pe_prescr_item_result a,
			pe_diagnostico c
	WHERE 	b.nr_seq_result = a.nr_seq_result
	AND 	a.nr_seq_prescr = nr_prescricao_p
	AND		a.nr_sequencia 	<> nr_seq_result_p
	and		b.nr_seq_diag	= nr_seq_diag_w
	AND 	c.nr_sequencia 	= b.nr_seq_diag
	AND 	c.ie_situacao 	= 'A'
	AND 	Obter_Se_Diag_Sae_Lib(nr_prescricao_p, b.nr_seq_diag) = 'S'
	AND 	coalesce(c.cd_estabelecimento, coalesce(wheb_usuario_pck.get_cd_estabelecimento, 0)) = coalesce(wheb_usuario_pck.get_cd_estabelecimento, 0);	
	
	if (ie_confirmado_w = 'C') and (qt_reg_w = 0)then
		CALL Desconfirmar_diagnostico(nr_prescricao_p, nr_seq_diag_w, nm_usuario_p);		
	end if;
	end;
end loop;
close c01;	

delete FROM triagem_classificatoria_sv where nr_seq_item_examinar = nr_seq_result_p;

delete	FROM pe_prescr_item_result
where	nr_seq_prescr	= nr_prescricao_p
and		nr_sequencia	= nr_seq_result_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
