CREATE OR REPLACE PROCEDURE elminar_outras_sessoes_tasy (nm_usuario_p text) AS $body$
DECLARE


sid_w		bigint;
serial_w	bigint;
nm_maquina_w	varchar(64);
osuser_w	varchar(30);

C01 CURSOR FOR
	SELECT	sid,
		serial#
	from	v$session
	where	upper(client_info) like 'USER='||Upper(nm_usuario_p)||';%'
  and audsid <> userenv('sessionid')
  and ( (machine <> nm_maquina_w) or (machine = nm_maquina_w and osuser <> osuser_w) );


BEGIN

select	machine, osuser
into STRICT	nm_maquina_w, osuser_w
from	v$session
	where	audsid = userenv('sessionid');

open C01;
loop
fetch C01 into
	sid_w,
	serial_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	CALL exec_sql_dinamico_bv('','begin TASY_KILL_SESSION('||sid_w||','||serial_w||'); end;','');
	end;
end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
