CREATE OR REPLACE PROCEDURE eme_alterar_propaci_regulacao (nr_seq_eme_regulacao_p bigint, nr_seq_eme_proced_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_sequencia_w		bigint;
nr_seq_proc_interno_w	bigint;
cd_procedimento_w	bigint;
qt_proced_w		integer;
ie_origem_proced_w	bigint;
nr_atendimento_w	bigint;
cd_convenio_w		bigint;


BEGIN 
 
if (nr_seq_eme_regulacao_p IS NOT NULL AND nr_seq_eme_regulacao_p::text <> '') then 
 
	select 	max(nr_atendimento) 
	into STRICT	nr_atendimento_w 
	from	eme_regulacao 
	where	nr_sequencia = nr_seq_eme_regulacao_p;
 
	select	max(nr_seq_proc_interno), 
		max(cd_procedimento), 
		max(coalesce(qt_proced,1)), 
		max(ie_origem_proced) 
	into STRICT	nr_seq_proc_interno_w, 
		cd_procedimento_w, 
		qt_proced_w, 
		ie_origem_proced_w 
	from	eme_regulacao_proced 
	where	nr_sequencia = nr_seq_eme_proced_p;
	 
	select	max(cd_convenio) 
	into STRICT	cd_convenio_w 
	from	atendimento_paciente_v 
	where	nr_atendimento = nr_atendimento_w;
			 
	if (nr_seq_proc_interno_w IS NOT NULL AND nr_seq_proc_interno_w::text <> '') then 
		SELECT * FROM Obter_Proc_Tab_Interno(nr_seq_proc_interno_w, null, nr_atendimento_w, null, cd_procedimento_w, ie_origem_proced_w, null, null) INTO STRICT cd_procedimento_w, ie_origem_proced_w;
	end if;
	 
	if (cd_procedimento_w IS NOT NULL AND cd_procedimento_w::text <> '') then 
	 
		select	coalesce(max(nr_sequencia),0) 
		into STRICT	nr_sequencia_w 
		from	procedimento_paciente a 
		where	a.nr_seq_reg_proced = nr_seq_eme_proced_p;
	 
		update	procedimento_paciente 
		set	cd_procedimento 	= cd_procedimento_w, 
			nr_seq_proc_interno 	= nr_seq_proc_interno_w, 
			qt_procedimento 	= qt_proced_w, 
			ie_origem_proced 	= ie_origem_proced_w 
		where	nr_sequencia 		= nr_sequencia_w;
		 
		CALL atualiza_preco_procedimento(nr_sequencia_w, cd_convenio_w, nm_usuario_p);
		 
	end if;
	 
end if;
	 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
