SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE eme_atualizar_contrato (nr_sequencia_p eme_regulacao.nr_sequencia%type, nm_usuario_p text) AS $body$
DECLARE


nr_seq_chamado_w	eme_chamado.nr_sequencia%type;
cd_cgc_w		pessoa_juridica.cd_cgc%type;
nr_seq_contrato_w	eme_contrato.nr_sequencia%type;


BEGIN

select 	max(nr_sequencia)
into STRICT	nr_seq_chamado_w
from 	eme_chamado
where 	nr_seq_regulacao = nr_sequencia_p;

if (nr_seq_chamado_w IS NOT NULL AND nr_seq_chamado_w::text <> '') then

	select 	max(cd_cgc)
	into STRICT	cd_cgc_w
	from	eme_regulacao
	where	nr_sequencia = nr_sequencia_p;

	select 	max(nr_sequencia)
	into STRICT	nr_seq_contrato_w
	from	eme_contrato
	where	cd_cgc = cd_cgc_w
	and	PKG_DATE_UTILS.START_OF(coalesce(dt_validade,clock_timestamp()), 'DAY') >= PKG_DATE_UTILS.START_OF(clock_timestamp(), 'DAY')
	and	PKG_DATE_UTILS.START_OF(dt_contrato, 'DAY') <= PKG_DATE_UTILS.START_OF(clock_timestamp(), 'DAY')
	and	cd_estabelecimento = obter_estabelecimento_ativo
	and (coalesce(dt_cancelamento::text, '') = '' or PKG_DATE_UTILS.START_OF(dt_cancelamento, 'DAY') > PKG_DATE_UTILS.START_OF(clock_timestamp(), 'DAY'));

	if (nr_seq_contrato_w IS NOT NULL AND nr_seq_contrato_w::text <> '') then
		update 	eme_chamado
		set 	nr_seq_contrato = nr_seq_contrato_w,
			dt_atualizacao = clock_timestamp(),
			nm_usuario = nm_usuario_p
		where 	nr_sequencia = nr_seq_chamado_w
		and (nr_seq_contrato <> nr_seq_contrato_w or coalesce(nr_seq_contrato::text, '') = '');
	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
