SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE eme_atualizar_obs_propaci ( nr_seq_eme_regulacao_p bigint, nr_seq_origem_p bigint, nr_seq_destino_p bigint, nm_usuario_p text) AS $body$
DECLARE



ds_observacao_w		varchar(255);
ds_origem_w		varchar(80) := '';
ds_destino_w		varchar(80) := '';
nr_seq_propaci_w	bigint;

C01 CURSOR FOR
	SELECT	b.nr_sequencia
	from	eme_regulacao_proced a,
		procedimento_paciente b
	where	a.nr_sequencia = b.nr_seq_reg_proced
	and	a.nr_seq_serv_eme = nr_seq_eme_regulacao_p;


BEGIN

if (nr_seq_eme_regulacao_p IS NOT NULL AND nr_seq_eme_regulacao_p::text <> '') then

	if (nr_seq_origem_p IS NOT NULL AND nr_seq_origem_p::text <> '') then
		ds_origem_w 	:= substr(obter_eme_origem_destino(nr_seq_origem_p,'D'),1,80);
	end if;
	if (nr_seq_destino_p IS NOT NULL AND nr_seq_destino_p::text <> '') then
		ds_destino_w	:= substr(obter_eme_origem_destino(nr_seq_destino_p,'D'),1,80);
	end if;

	ds_observacao_w := wheb_mensagem_pck.get_texto(796506) || ' ' || ds_origem_w || ', ' || wheb_mensagem_pck.get_texto(796509) || ': ' ||ds_destino_w;

	open C01;
	loop
	fetch C01 into
		nr_seq_propaci_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		update	procedimento_paciente
		set	ds_observacao = ds_observacao_w,
			dt_atualizacao = clock_timestamp(),
			nm_usuario = nm_usuario_p
		where	nr_sequencia = nr_seq_propaci_w;

		end;
	end loop;
	close C01;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
