CREATE OR REPLACE PROCEDURE eme_atualiza_chamado_reg ( nr_sequencia_p bigint, cd_estabelecimento_p text, nm_usuario_p text) AS $body$
DECLARE


cd_medico_resp_w				pessoa_fisica.cd_pessoa_fisica%type;
cd_medico_intervencionista_w	pessoa_fisica.cd_pessoa_fisica%type;
nr_seq_chamado_w				eme_chamado.nr_sequencia%type;
cd_externo_w					eme_chamado.cd_externo%type;


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	select	max(nr_sequencia)
	into STRICT	nr_seq_chamado_w
	from	eme_chamado
	where	nr_seq_regulacao = nr_sequencia_p;

	if (nr_seq_chamado_w IS NOT NULL AND nr_seq_chamado_w::text <> '') then

		select	max(cd_medico_resp),
				max(cd_medico_intervencionista),
				max(cd_externo)
		into STRICT	cd_medico_resp_w,
				cd_medico_intervencionista_w,
				cd_externo_w
		from	eme_regulacao
		where	nr_sequencia = nr_sequencia_p;

		update 	eme_chamado
		set 	cd_medico_regulador	= cd_medico_resp_w,
				cd_medico_resp		= cd_medico_intervencionista_w,
				nm_usuario			= nm_usuario_p,
				dt_atualizacao		= clock_timestamp(),
				cd_estabelecimento	= cd_estabelecimento_p,
				cd_externo          = cd_externo_w
		where	nr_sequencia 		= nr_seq_chamado_w;

	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
