SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE eme_atualiza_chamado_resp ( nr_sequencia_p bigint, cd_estabelecimento_p text, nm_usuario_p text) AS $body$
DECLARE

dt_partida_w		timestamp;
dt_chegada_local_w	timestamp;
dt_saida_local_w	timestamp;
dt_chegada_base_w	timestamp;
qt_km_inicial_w		eme_reg_resposta.qt_km_inicial%type;
qt_km_final_w		eme_reg_resposta.qt_km_final%type;
nr_seq_unidade_w	eme_veiculo.nr_sequencia%type;
nr_seq_origem_w		eme_reg_resposta.nr_seq_origem%type;
nr_seq_destino_w	eme_origem_destino.nr_sequencia%type;
nr_seq_chamado_w	eme_chamado.nr_sequencia%type;
nr_seq_regulacao_w	eme_regulacao.nr_sequencia%type;


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	select	max(nr_seq_reg)
	into STRICT	nr_seq_regulacao_w
	from	eme_reg_resposta
	where	nr_sequencia = nr_sequencia_p;

	if (nr_seq_regulacao_w IS NOT NULL AND nr_seq_regulacao_w::text <> '') then

		select 	max(nr_sequencia)
		into STRICT	nr_seq_chamado_w
		from	eme_chamado
		where	nr_seq_regulacao = nr_seq_regulacao_w;

		if (nr_seq_chamado_w IS NOT NULL AND nr_seq_chamado_w::text <> '') then

			select	max(dt_partida),
				max(dt_chegada_local),
				max(dt_saida_local),
				max(dt_chegada_base),
				max(qt_km_inicial),
				max(qt_km_final),
				max(nr_seq_unidade),
				max(nr_seq_origem),
				max(nr_seq_destino)
			into STRICT	dt_partida_w,
				dt_chegada_local_w,
				dt_saida_local_w,
				dt_chegada_base_w,
				qt_km_inicial_w,
				qt_km_final_w,
				nr_seq_unidade_w,
				nr_seq_origem_w,
				nr_seq_destino_w
			from	eme_reg_resposta
			where	nr_sequencia = nr_sequencia_p;

			update 	eme_chamado
			set 	dt_saida		= dt_partida_w,
				dt_inicio_atend		= dt_chegada_local_w,
				dt_fim_atend		= dt_saida_local_w,
				dt_retorno		= dt_chegada_base_w,
				qt_km_saida		= qt_km_inicial_w,
				qt_km_retorno		= qt_km_final_w,
				nr_seq_veiculo		= nr_seq_unidade_w,
				nr_seq_origem		= nr_seq_origem_w,
				nr_seq_destino		= nr_seq_destino_w,
				nm_usuario		= nm_usuario_p,
				dt_atualizacao		= clock_timestamp(),
				cd_estabelecimento	= cd_estabelecimento_p
			where	nr_sequencia = nr_seq_chamado_w;

		end if;

	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
