CREATE OR REPLACE PROCEDURE eme_atua_fatura_valor (nr_faturamento_p bigint, dt_emissao_p timestamp, dt_competencia_p timestamp, nr_seq_contrato_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
ie_dt_competencia_w	varchar(1);
dt_limite_chamado_w	timestamp := dt_emissao_p;

BEGIN
	ie_dt_competencia_w := obter_param_usuario(929, 25, obter_perfil_ativo, Wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_dt_competencia_w);
	 
	if (ie_dt_competencia_w = 'S') then 
		select	last_day(dt_competencia_p) 
		into STRICT	dt_limite_chamado_w 
		;
	end if;
 
	update	eme_chamado 
	set	nr_seq_faturamento 	= nr_faturamento_p, 
		ie_faturamento 		= 'N' 
	where	nr_seq_contrato = nr_seq_contrato_p 
	and	ie_faturamento = 'S' 
	and	coalesce(nr_seq_faturamento::text, '') = '' 
	and	TRUNC(dt_chamado) <= TRUNC(dt_limite_chamado_w);
 
	update 	eme_faturamento 
	set	vl_fatura = vl_fatura + coalesce(eme_obter_valor_fatura(nr_seq_contrato_p, nr_faturamento_p),0) 
	where	nr_sequencia = nr_faturamento_p;
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
