SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE eme_consiste_gerar_atendimento ( nr_chamado_p bigint, ds_erro_p INOUT text) AS $body$
DECLARE



ds_erro_consiste_w			varchar(2000);
nr_seq_contrato_w			bigint;
cd_convenio_w			integer;
cd_categoria_w			varchar(10);
cd_medico_w			varchar(10);
cd_medico_regulador_w		varchar(10);
cd_pessoa_fisica_w		varchar(10);
cd_cgc_w			varchar(14);
nr_seq_veiculo_w			bigint;
cd_setor_atendimento_w		integer;
cd_unidade_basica_w		varchar(10);
cd_unidade_compl_w		varchar(10);


BEGIN

select	nr_seq_contrato
into STRICT	nr_seq_contrato_w
from	eme_chamado
where	nr_sequencia = nr_chamado_p;


select	coalesce(max(cd_convenio),null),
	coalesce(max(cd_categoria),null),
	coalesce(max(cd_cgc),null)
into STRICT	cd_convenio_w,
	cd_categoria_w,
	cd_cgc_w
from	eme_contrato
where	nr_sequencia = nr_seq_contrato_w;

if (coalesce(cd_convenio_w::text, '') = '') and (cd_cgc_w IS NOT NULL AND cd_cgc_w::text <> '') then
	ds_erro_consiste_w	:= ds_erro_consiste_w || WHEB_MENSAGEM_PCK.get_texto(277583);
end if;
if (coalesce(cd_categoria_w::text, '') = '') and (cd_cgc_w IS NOT NULL AND cd_cgc_w::text <> '') then
	ds_erro_consiste_w	:= ds_erro_consiste_w || WHEB_MENSAGEM_PCK.get_texto(277584);
end if;

select	coalesce(max(cd_medico_resp),null),
	coalesce(max(cd_medico_regulador),null),
	coalesce(max(nr_seq_veiculo),null),
	coalesce(max(cd_pessoa_fisica),null)
into STRICT	cd_medico_w,
	cd_medico_regulador_w,
	nr_seq_veiculo_w,
	cd_pessoa_fisica_w
from	eme_chamado
where	nr_sequencia = nr_chamado_p;

if (coalesce(cd_pessoa_fisica_w::text, '') = '') then
	ds_erro_consiste_w	:= ds_erro_consiste_w || WHEB_MENSAGEM_PCK.get_texto(277585);
end if;
if (coalesce(cd_medico_w::text, '') = '') and (coalesce(cd_medico_regulador_w::text, '') = '') then
	ds_erro_consiste_w	:= ds_erro_consiste_w || WHEB_MENSAGEM_PCK.get_texto(277586);
end if;
if (coalesce(nr_seq_veiculo_w::text, '') = '') then
	ds_erro_consiste_w	:= ds_erro_consiste_w || WHEB_MENSAGEM_PCK.get_texto(277587);
else
	begin
	select	cd_setor_atendimento
	into STRICT	cd_setor_atendimento_w
	from	eme_veiculo
	where	nr_sequencia = nr_seq_veiculo_w;

	if (coalesce(cd_setor_atendimento_w::text, '') = '') then
		ds_erro_consiste_w	:= ds_erro_consiste_w || WHEB_MENSAGEM_PCK.get_texto(277588);
	else
		begin
		select	coalesce(max(cd_unidade_basica),0),
			coalesce(max(cd_unidade_compl),0)
		into STRICT	cd_unidade_basica_w,
			cd_unidade_compl_w
		from	unidade_atend_livre_v
		where	cd_setor_atendimento = cd_setor_atendimento_w;

		if (cd_unidade_basica_w = '0') then
			ds_erro_consiste_w	:= ds_erro_consiste_w || WHEB_MENSAGEM_PCK.get_texto(277589);
		elsif (cd_unidade_compl_w = '0') then
			ds_erro_consiste_w	:= ds_erro_consiste_w || WHEB_MENSAGEM_PCK.get_texto(277590);
		end if;
		end;
	end if;
	end;
end if;

ds_erro_p	:= substr(ds_erro_consiste_w,1,255);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
