CREATE OR REPLACE PROCEDURE eme_duplicar_contrato ( nr_seq_contrato_p bigint, cd_contrato_p text, dt_contrato_p timestamp, qt_dia_vencimento_p text, nr_seq_regra_preco_p bigint, nr_seq_cobertura_p bigint, ie_reajuste_p text, nm_usuario_p text, nr_sequencia_p INOUT bigint) AS $body$
DECLARE


nr_seq_preco_w			bigint;
dt_vigencia_w			timestamp;
qt_minima_w			integer;
qt_maxima_w			integer;
vl_preco_w			bigint;
nr_seq_pf_contrato_w		bigint;
nr_seq_tipo_servico_w		bigint;
cd_pessoa_fisica_w		varchar(10);
cd_codigo_convenio_w		varchar(30);
dt_cancelamento_w		timestamp;
dt_inclusao_w			timestamp;
cd_pf_titular_w			varchar(10);
nr_seq_contrato_doc_w		bigint;
ds_documento_w			text;
vl_fixo_w		double precision;

C01 CURSOR FOR
SELECT	dt_vigencia,
	qt_minima,
	qt_maxima,
	vl_preco,
	vl_fixo
from	eme_contrato_preco
where	nr_seq_contrato = nr_seq_contrato_p;

C02 CURSOR FOR
SELECT	nr_seq_tipo_servico,
	cd_pessoa_fisica,
	cd_codigo_convenio,
	dt_cancelamento,
	dt_inclusao,
	cd_pf_titular
from	eme_pf_contrato
where	nr_seq_contrato = nr_seq_contrato_p;


BEGIN

select	nextval('eme_contrato_seq')
into STRICT	nr_sequencia_p
;

insert into eme_contrato(
	nr_sequencia,
	cd_estabelecimento,
	cd_contrato,
	nr_seq_cobertura,
	dt_contrato,
	cd_cgc,
	cd_pessoa_fisica,
	nr_seq_regra_preco,
	qt_dia_vencimento,
	ie_reajuste,
	dt_atualizacao,
	nm_usuario)
SELECT	nr_sequencia_p,
	cd_estabelecimento,
	coalesce(cd_contrato_p,nr_sequencia_p),
	coalesce(nr_seq_cobertura_p,nr_seq_cobertura),
	dt_contrato_p,
	cd_cgc,
	cd_pessoa_fisica,
	coalesce(nr_seq_regra_preco_p,nr_seq_regra_preco),
	coalesce(qt_dia_vencimento_p,qt_dia_vencimento),
	coalesce(ie_reajuste_p,ie_reajuste),
	clock_timestamp(),
	nm_usuario_p
from	eme_contrato
where	nr_sequencia = nr_seq_contrato_p;

OPEN C01;
LOOP
FETCH C01 into
	dt_vigencia_w,
	qt_minima_w,
	qt_maxima_w,
	vl_preco_w,
	vl_fixo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select 	nextval('eme_contrato_preco_seq')
	into STRICT	nr_seq_preco_w
	;

	insert into eme_contrato_preco(
		nr_sequencia,
		nr_seq_contrato,
		dt_atualizacao,
		nm_usuario,
		dt_vigencia,
		qt_minima,
		qt_maxima,
		vl_preco,
		vl_fixo)
	values (
		nr_seq_preco_w,
		nr_sequencia_p,
		clock_timestamp(),
		nm_usuario_p,
		dt_vigencia_w,
		qt_minima_w,
		qt_maxima_w,
		vl_preco_w,
		vl_fixo_w);
	end;
END LOOP;
close c01;

OPEN C02;
LOOP
FETCH C02 into
	nr_seq_tipo_servico_w,
	cd_pessoa_fisica_w,
	cd_codigo_convenio_w,
	dt_cancelamento_w,
	dt_inclusao_w,
	cd_pf_titular_w;
EXIT WHEN NOT FOUND; /* apply on C02 */
	begin
	select	nextval('eme_pf_contrato_seq')
	into STRICT	nr_seq_pf_contrato_w
	;
	insert into eme_pf_contrato(
		nr_sequencia,
		nr_seq_contrato,
		nr_seq_tipo_servico,
		dt_atualizacao,
		nm_usuario,
		cd_pessoa_fisica,
		cd_codigo_convenio,
		dt_cancelamento,
		dt_inclusao,
		cd_pf_titular)
	values (
		nr_seq_pf_contrato_w,
		nr_sequencia_p,
		nr_seq_tipo_servico_w,
		clock_timestamp(),
		nm_usuario_p,
		cd_pessoa_fisica_w,
		cd_codigo_convenio_w,
		dt_cancelamento_w,
		dt_inclusao_w,
		cd_pf_titular_w);
	end;
END LOOP;
close c02;

begin
select	ds_documento
into STRICT	ds_documento_w
from	eme_contrato_doc
where	nr_seq_contrato = nr_seq_contrato_p;
exception
	when others then
		 ds_documento_w := null;
end;

if (ds_documento_w IS NOT NULL AND ds_documento_w::text <> '') then
	begin
	select	nextval('eme_contrato_doc_seq')
	into STRICT	nr_seq_contrato_doc_w
	;

	insert into eme_contrato_doc(
		nr_sequencia,
		nr_seq_contrato,
		dt_atualizacao,
		nm_usuario,
		ds_documento)
	values (
		nr_seq_contrato_doc_w,
		nr_sequencia_p,
		clock_timestamp(),
		nm_usuario_p,
		ds_documento_w);
	end;
end if;

commit;

end;
$body$
