CREATE OR REPLACE PROCEDURE eme_excluir_chamado ( nr_sequencia_p bigint, nm_usuario_p text, ds_erro_p INOUT text) AS $body$
DECLARE

 
nr_atendimento_w	integer;


BEGIN 
 
select	coalesce(nr_atendimento,0) 
into STRICT	nr_atendimento_w 
from	eme_chamado 
where	nr_sequencia = nr_sequencia_p;
 
if (nr_atendimento_w <> 0) then 
	begin 
	update	eme_chamado 
	set	nr_atendimento  = NULL 
	where	nr_sequencia = nr_sequencia_p;
 
	ds_erro_p := EXCLUIR_ATENDIMENTO(nr_atendimento_w, 'S', nm_usuario_p, null);
	if (ds_erro_p <> '') or (ds_erro_p IS NOT NULL AND ds_erro_p::text <> '') then 
		update	eme_chamado 
		set	nr_atendimento = nr_atendimento_w 
		where	nr_sequencia = nr_sequencia_p;
	end if;
	end;
end if;
 
commit;
 
end;
