CREATE OR REPLACE PROCEDURE eme_gerar_pergunta_regulacao ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w		bigint;
qt_existe_w		integer;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	eme_grav_pergunta
	where	ie_situacao = 'A';


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	eme_reg_grav_resp
where	nr_seq_regulacao = nr_sequencia_p;

OPEN C01;
LOOP
FETCH C01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	insert into eme_reg_grav_resp(
		nr_sequencia,
		nr_seq_regulacao,
		dt_atualizacao,
		nm_usuario,
		nr_seq_pergunta,
		ie_resposta)
	values (nextval('eme_reg_grav_resp_seq'),
		nr_sequencia_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_sequencia_w,
		'NI');
	end;
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
