SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE eme_importa_endereco ( cd_unimed_p text, cd_empresa_p text, cd_familia_p text, cd_dependencia_p text, nm_usuario_p text, ds_endereco_p text, nr_endereco_p text, ds_complemento_p text, ds_bairro_p text, cd_cep_p text, nr_telefone_p text, ds_email_p text, ds_municipio_p text, sg_estado_p text) AS $body$
DECLARE



cd_pessoa_fisica_w		varchar(10);
cd_pessoa_compl_w		varchar(10);
cd_codigo_convenio_w		varchar(30);
nr_sequencia_w			smallint;
tag_pais_w			varchar(15);


BEGIN
cd_codigo_convenio_w	:= cd_unimed_p || cd_empresa_p || cd_familia_p || cd_dependencia_p;

select	max(ds_locale)
into STRICT	tag_pais_w
from	user_locale
where	nm_user = nm_usuario_p;

select	coalesce(max(cd_pessoa_fisica),0)
into STRICT	cd_pessoa_fisica_w
from	eme_pf_contrato
where	cd_codigo_convenio = cd_codigo_convenio_w;

if (cd_pessoa_fisica_w <> 0) then
	begin

	select	coalesce(max(cd_pessoa_fisica),0)
	into STRICT	cd_pessoa_compl_w
	from	compl_pessoa_fisica
	where	cd_pessoa_fisica = cd_pessoa_fisica_w;

	if (cd_pessoa_compl_w = 0) then
		begin

		select (coalesce(max(nr_sequencia),0) + 1)
		into STRICT	nr_sequencia_w
		from	compl_pessoa_fisica
		where	cd_pessoa_fisica = cd_pessoa_fisica_w;

		if (tag_pais_w = 'de_DE') then
			insert	into compl_pessoa_fisica(
				cd_pessoa_fisica,
				nr_sequencia,
				ie_tipo_complemento,
				dt_atualizacao,
				nm_usuario,
				ds_endereco,
				ds_compl_end,
				ds_complemento,
				ds_bairro,
				cd_cep,
				nr_telefone,
				ds_email,
				ds_municipio,
				sg_estado)
			values (
				cd_pessoa_fisica_w,
				nr_sequencia_w,
				1,
				clock_timestamp(),
				nm_usuario_p,
				ds_endereco_p,
				nr_endereco_p,
				ds_complemento_p,
				ds_bairro_p,
				cd_cep_p,
				substr(nr_telefone_p,1,15),
				ds_email_p,
				ds_municipio_p,
				sg_estado_p);

		else
			insert	into compl_pessoa_fisica(
				cd_pessoa_fisica,
				nr_sequencia,
				ie_tipo_complemento,
				dt_atualizacao,
				nm_usuario,
				ds_endereco,
				nr_endereco,
				ds_complemento,
				ds_bairro,
				cd_cep,
				nr_telefone,
				ds_email,
				ds_municipio,
				sg_estado)
			values (
				cd_pessoa_fisica_w,
				nr_sequencia_w,
				1,
				clock_timestamp(),
				nm_usuario_p,
				ds_endereco_p,
				nr_endereco_p,
				ds_complemento_p,
				ds_bairro_p,
				cd_cep_p,
				substr(nr_telefone_p,1,15),
				ds_email_p,
				ds_municipio_p,
				sg_estado_p);
		end if;
		end;
	else
		if (tag_pais_w = 'de_DE')then
			update	compl_pessoa_fisica
			set	ds_endereco = ds_endereco_p,
				ds_compl_end = nr_endereco_p,
				ds_complemento	= ds_complemento_p,
				ds_bairro = ds_bairro_p,
				cd_cep = cd_cep_p,
				nr_telefone = substr(nr_telefone_p,1,15),
				ds_email = ds_email_p,
				sg_estado = sg_estado_p	,
				ds_municipio = ds_municipio_p,
				nm_usuario = nm_usuario_p,
				dt_atualizacao = clock_timestamp()
			where	cd_pessoa_fisica = cd_pessoa_fisica_w;
		else
			update	compl_pessoa_fisica
			set	ds_endereco = ds_endereco_p,
				nr_endereco = nr_endereco_p,
				ds_complemento	= ds_complemento_p,
				ds_bairro = ds_bairro_p,
				cd_cep = cd_cep_p,
				nr_telefone = substr(nr_telefone_p,1,15),
				ds_email = ds_email_p,
				sg_estado = sg_estado_p	,
				ds_municipio = ds_municipio_p,
				nm_usuario = nm_usuario_p,
				dt_atualizacao = clock_timestamp()
			where	cd_pessoa_fisica = cd_pessoa_fisica_w;
		end if;

	end if;
	end;
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
