CREATE OR REPLACE PROCEDURE eme_insere_km_controle_resp ( nr_seq_resposta_p bigint, qt_km_p bigint, ie_opcao_p bigint) AS $body$
BEGIN
if (nr_seq_resposta_p IS NOT NULL AND nr_seq_resposta_p::text <> '') and (qt_km_p IS NOT NULL AND qt_km_p::text <> '') then
	if (ie_opcao_p = 1) then
		update	eme_reg_resposta
		set	qt_km_inicial = qt_km_p
		where	nr_sequencia = nr_seq_resposta_p;
	elsif (ie_opcao_p = 2) then
		update	eme_reg_resposta
		set	qt_km_final = qt_km_p
		where	nr_sequencia = nr_seq_resposta_p;
	end if;
end if;

commit;

end;
$body$
