CREATE OR REPLACE PROCEDURE eme_inserir_anexos ( nr_seq_contrato_p bigint, ds_diretorio_padrao_p text, cd_estabelecimento_p text, nm_usuario_p text, ds_arquivo_p INOUT text) AS $body$
DECLARE


ds_arquivo_w		varchar(500);
nr_sequencia_w		eme_anexo.nr_sequencia%type;
ds_diretorio_arquivo_w	varchar(500);


BEGIN

if (nr_seq_contrato_p IS NOT NULL AND nr_seq_contrato_p::text <> '') then

	select	nextval('eme_anexo_seq')
	into STRICT	nr_sequencia_w
	;

	ds_arquivo_w	:= 'Aditivo_Contrato_' || nr_seq_contrato_p || '_anexo_' || nr_sequencia_w || '.pdf';
	ds_diretorio_arquivo_w	:= ds_diretorio_padrao_p || '\' ||ds_arquivo_w; --'
	insert into eme_anexo(	ds_arquivo,
				ds_titulo,
				dt_atualizacao,
				dt_atualizacao_nrec,
				nm_usuario,
				nm_usuario_nrec,
				nr_seq_contrato,
				nr_sequencia)
			values (	ds_diretorio_arquivo_w,
				ds_arquivo_w,
				clock_timestamp(),
				clock_timestamp(),
				nm_usuario_p,
				nm_usuario_p,
				nr_seq_contrato_p,
				nr_sequencia_w);

end if;

commit;

ds_arquivo_p := ds_arquivo_w;

end;
$body$
