CREATE OR REPLACE PROCEDURE eme_inserir_atend_hc ( nr_seq_regulacao_p bigint, nr_seq_resposta_p bigint, qt_km_p bigint, ie_opcao_p bigint, nm_usuario_p text) AS $body$
DECLARE


/* ie_opcao_p
   1 = sa√≠da
   2 = chegada*/
nr_atendimento_w	bigint;
cd_pessoa_fisica_w	varchar(10);
nr_seq_paciente_w	bigint;
cd_medico_w		varchar(10);
nr_seq_atend_hc_w	bigint;

BEGIN
if (nr_seq_regulacao_p IS NOT NULL AND nr_seq_regulacao_p::text <> '') and (qt_km_p IS NOT NULL AND qt_km_p::text <> '') then

	select	max(nr_atendimento),
		max(cd_pessoa_fisica)
	into STRICT	nr_atendimento_w,
		cd_pessoa_fisica_w
	from	eme_regulacao
	where	nr_sequencia = nr_seq_regulacao_p;

	select	max(nr_sequencia)
	into STRICT	nr_seq_paciente_w
	from	paciente_home_care
	where	cd_pessoa_fisica = cd_pessoa_fisica_w
	and	((trunc(dt_final) >= trunc(clock_timestamp()))
	or (coalesce(dt_final::text, '') = ''));


	if (ie_opcao_p = 1) then

		select	max(cd_medico)
		into STRICT	cd_medico_w
		from	eme_reg_resposta
		where	nr_sequencia = nr_seq_resposta_p;

		if (nr_seq_paciente_w IS NOT NULL AND nr_seq_paciente_w::text <> '') then

			insert into paciente_hc_atend(
				nr_sequencia,
				nr_seq_paciente,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				dt_atendimento,
				qt_km_inicial,
				nr_atendimento,
				cd_funcionario,
				ie_tipo_atendimento)
			values (	nextval('paciente_hc_atend_seq'),
				nr_seq_paciente_w,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				qt_km_p,
				nr_atendimento_w,
				cd_medico_w,
				'V');
		end if;
	elsif (ie_opcao_p = 2) and (nr_seq_paciente_w IS NOT NULL AND nr_seq_paciente_w::text <> '') then

		select	max(nr_sequencia)
		into STRICT	nr_seq_atend_hc_w
		from	paciente_hc_atend
		where	nr_seq_paciente = nr_seq_paciente_w;

		if (nr_seq_atend_hc_w IS NOT NULL AND nr_seq_atend_hc_w::text <> '') then
			update	paciente_hc_atend
			set	qt_km_final = qt_km_p
			where	nr_sequencia = nr_seq_atend_hc_w;
		end if;

	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE eme_inserir_atend_hc ( nr_seq_regulacao_p bigint, nr_seq_resposta_p bigint, qt_km_p bigint, ie_opcao_p bigint, nm_usuario_p text) FROM PUBLIC;

