CREATE OR REPLACE PROCEDURE eme_inserir_integrante_equipe ( NR_SEQ_RESPOSTA_P bigint, CD_PESSOA_FISICA_P text, NM_USUARIO_P text) AS $body$
DECLARE

ie_possui_pf_w		varchar(1);

BEGIN
select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ie_possui_pf_w
from	EME_REG_RESP_PARTIC
where	NR_SEQ_RESPOSTA = NR_SEQ_RESPOSTA_P
and		cd_pessoa_fisica = cd_pessoa_fisica_p;


if (ie_possui_pf_w = 'N') then
	insert into EME_REG_RESP_PARTIC(
			NR_SEQUENCIA,
			DT_ATUALIZACAO,
			NM_USUARIO,
			CD_PESSOA_FISICA,
			NR_SEQ_RESPOSTA )
		values (
			nextval('eme_reg_resp_partic_seq'),
			clock_timestamp(),
			nm_usuario_p,
			cd_pessoa_fisica_p,
			nr_seq_resposta_p);
end if;

commit;

end;
