CREATE OR REPLACE PROCEDURE eme_log_email_boletos (nm_usuario_p text, nr_seq_faturamento_p bigint, nr_seq_contrato_p bigint) AS $body$
DECLARE


ds_contato_w	eme_contrato_email.ds_contato%type;
ds_email_w		eme_contrato_email.ds_email%type;

c01 CURSOR FOR
SELECT  a.ds_contato, a.ds_email
from eme_contrato_email a,
    eme_contrato b
where a.nr_seq_contrato = nr_seq_contrato_p
and b.nr_sequencia = a.nr_seq_contrato;


BEGIN

open c01;
loop fetch c01 into
	ds_contato_w,
	ds_email_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	insert into eme_fat_boleto_log(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_faturamento,
		ds_contato,
		ds_email)
	values (
		nextval('eme_fat_boleto_log_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_faturamento_p,
		ds_contato_w,
		ds_email_w);

end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
