CREATE OR REPLACE PROCEDURE eme_obter_dados_segurado_pf (cd_pessoa_fisica_p text, cd_estabelecimento_p bigint, nm_usuario_p text, nr_seq_segurado_p INOUT bigint, cd_usuario_plano_p INOUT text, dt_valida_carteira_p INOUT timestamp, qt_seg_cadast_p INOUT bigint) AS $body$
DECLARE


nr_seq_segurado_w	pls_segurado.nr_sequencia%type;
cd_usuario_plano_w	varchar(30);
qt_seg_cadast_w		bigint;


BEGIN

	select	COUNT(1)
	into STRICT	qt_seg_cadast_w
	from	pls_segurado a
	where	a.cd_pessoa_fisica	= cd_pessoa_fisica_p
	and   	pls_obter_dados_segurado(a.nr_sequencia, 'CS') = 'A'
	and		a.ie_tipo_segurado = 'B'
	and   ie_situacao_atend = 'A';

	if (qt_seg_cadast_w = 1) then

		select	a.nr_sequencia
		into STRICT	nr_seq_segurado_w
		from	pls_segurado a
		where	a.cd_pessoa_fisica	= cd_pessoa_fisica_p
		and   	pls_obter_dados_segurado(a.nr_sequencia, 'CS') = 'A'
		and		a.ie_tipo_segurado = 'B'
		and   ie_situacao_atend = 'A';

		cd_usuario_plano_w 	:= substr(pls_obter_dados_segurado(nr_seq_segurado_w,'C'),1,30);
		dt_valida_carteira_p	:= pls_obter_validade_carteira(cd_usuario_plano_w, cd_estabelecimento_p);
		nr_seq_segurado_p	:= nr_seq_segurado_w;
		cd_usuario_plano_p	:= cd_usuario_plano_w;

	end if;

qt_seg_cadast_p	:= qt_seg_cadast_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
