CREATE OR REPLACE PROCEDURE eme_reg_evo_after_post_hist ( nm_usuario_p text, cd_pessoa_fisica_p text, nr_seq_regulacao_p bigint) AS $body$
DECLARE

ie_medico_w	varchar(1);
ie_regulacao_w	varchar(1);

BEGIN
if (nr_seq_regulacao_p IS NOT NULL AND nr_seq_regulacao_p::text <> '') then
	begin
	select	CASE WHEN coalesce(count(*),0)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_medico_w
	from	medico
	where	cd_pessoa_fisica = cd_pessoa_fisica_p
	and	ie_situacao = 'A';

	select	CASE WHEN coalesce(count(*),0)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_regulacao_w
	from	eme_regulacao
	where	nr_sequencia = nr_seq_regulacao_p
	and	coalesce(cd_medico_resp::text, '') = '';

	if (ie_medico_w = 'S') and (ie_regulacao_w = 'S') then
		begin
		update	eme_regulacao
		set	ie_alteracao_medico	= 'N',
			nm_usuario		= nm_usuario_p,
			dt_atualizacao		= clock_timestamp(),
			cd_medico_resp		= cd_pessoa_fisica_p,
			cd_pessoa_origem_medico = cd_pessoa_fisica_p
		where	nr_sequencia 		= nr_seq_regulacao_p;
		end;
	else
		begin
		update	eme_regulacao
		set	ie_alteracao_medico	= 'N',
			nm_usuario		= nm_usuario_p,
			dt_atualizacao		= clock_timestamp(),
			cd_pessoa_origem_medico	= cd_pessoa_fisica_p
		where	nr_sequencia		= nr_seq_regulacao_p;
		end;
	end if;
	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
