SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE encaminhar_proc_aprov_limite ( nr_seq_aprovacao_p bigint, nr_seq_processo_p bigint, nr_seq_regra_p bigint, nm_usuario_p text) AS $body$
DECLARE

	
cd_aprovador_w			regra_limite_compras_aprov.cd_aprovador%type;
ie_tipo_w				processo_aprov_compra.ie_tipo%type;
ie_permite_sc_w			regra_limite_compras_aprov.ie_permite_sc%type;
ie_permite_cc_w			regra_limite_compras_aprov.ie_permite_cc%type;
ie_permite_oc_w			regra_limite_compras_aprov.ie_permite_oc%type;
ie_permite_st_w        regra_limite_compras_aprov.ie_permite_st%type;
ie_possui_aprov_enc_w	varchar(1) := 'N';
nr_documento_w          processo_aprov_compra.nr_documento%type;
ie_tipo_ordem_w         ordem_compra.ie_tipo_ordem%type;
	
c01 CURSOR FOR
SELECT distinct cd_aprovador,
		coalesce(ie_permite_sc,'N'),
		coalesce(ie_permite_cc,'N'),
		coalesce(ie_permite_oc,'N'),
        coalesce(ie_permite_st,'N')
from (
	SELECT	distinct cd_aprovador,
			ie_permite_sc,
			ie_permite_cc,
			ie_permite_oc,
            ie_permite_st
	from	regra_limite_compras_aprov
	where	nr_seq_regra = nr_seq_regra_p
	
union all

	select	obter_pessoa_delegacao(cd_aprovador,'AC',trunc(clock_timestamp(),'dd')),
			ie_permite_sc,
			ie_permite_cc,
			ie_permite_oc,
            ie_permite_st
	from	regra_limite_compras_aprov
	where	nr_seq_regra = nr_seq_regra_p) alias7
where	(cd_aprovador IS NOT NULL AND cd_aprovador::text <> '');
					

BEGIN

select 	ie_tipo, nr_documento
into STRICT	ie_tipo_w, nr_documento_w
from 	processo_aprov_compra where nr_sequencia = nr_seq_aprovacao_p LIMIT 1;

if (ie_tipo_w = 'O') then

    select ie_tipo_ordem
    into STRICT ie_tipo_ordem_w
    from ordem_compra
    where nr_ordem_compra = nr_documento_w;

end if;

open c01;
loop
fetch c01 into	
	cd_aprovador_w,
	ie_permite_sc_w,
	ie_permite_cc_w,
	ie_permite_oc_w,
    ie_permite_st_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin	
	if 	((ie_permite_sc_w = 'S' AND ie_tipo_w = 'S') or
		(ie_permite_cc_w = 'S' AND ie_tipo_w = 'C') or
		((ie_permite_oc_w = 'S') and (ie_tipo_w = 'O') and (ie_tipo_ordem_w <> 'T')) or
        ((ie_permite_st_w = 'S') and (ie_tipo_w = 'O') and (ie_tipo_ordem_w = 'T'))) then
		CALL encaminhar_proc_aprov_compra(nr_seq_aprovacao_p,nr_seq_processo_p,null,nm_usuario_p,wheb_mensagem_pck.get_texto(289129),cd_aprovador_w,'S');
		ie_possui_aprov_enc_w := 'S';
	end if;
	end;
end loop;
close c01;

if (ie_possui_aprov_enc_w = 'N') then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(817750);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
