CREATE OR REPLACE PROCEDURE encaminhar_req_negocio ( NR_SEQUENCIA_P LATAM_REQUISITO_HISTORICO.NR_SEQUENCIA%TYPE, IE_LIBERAR_DESENV_P LATAM_REQUISITO_HISTORICO.IE_LIBERAR_DESENV%TYPE, DS_HISTORICO_P LATAM_REQUISITO_HISTORICO.DS_HISTORICO%TYPE, IE_SITUACAO_P LATAM_REQUISITO_HISTORICO.IE_SITUACAO%TYPE default null) AS $body$
DECLARE

    nm_usuario_s            CONSTANT  LATAM_REQUISITO_HISTORICO.NM_USUARIO%TYPE     := WHEB_USUARIO_PCK.GET_NM_USUARIO;
    dt_atual_s              CONSTANT  LATAM_REQUISITO_HISTORICO.DT_ATUALIZACAO%TYPE := clock_timestamp();
    ie_liberar_desenv_old_w LATAM_REQUISITO.IE_LIBERAR_DESENV%type;

BEGIN
    INSERT INTO LATAM_REQUISITO_HISTORICO(
        NR_SEQUENCIA,
        DT_ATUALIZACAO,
        NM_USUARIO,
        DT_ATUALIZACAO_NREC,
        NM_USUARIO_NREC,
        IE_LIBERAR_DESENV,
        DT_LIBERACAO,
        DS_HISTORICO,
        NR_SEQ_REQUISITO,
        IE_SITUACAO)
    SELECT nextval('latam_requisito_historico_seq'), 
           dt_atual_s, 
           nm_usuario_s, 
           DT_ATUALIZACAO_NREC, 
           NM_USUARIO_NREC,
           IE_LIBERAR_DESENV,
           DT_LIBERACAO,
           DS_HISTORICO_P,
           NR_SEQUENCIA_P,
           IE_SITUACAO_P
    FROM   LATAM_REQUISITO 
    WHERE  NR_SEQUENCIA = NR_SEQUENCIA_P;

    SELECT IE_LIBERAR_DESENV
    INTO STRICT   ie_liberar_desenv_old_w
    FROM   LATAM_REQUISITO
    WHERE  NR_SEQUENCIA      = NR_SEQUENCIA_P;

    UPDATE LATAM_REQUISITO
    SET    IE_LIBERAR_DESENV = IE_LIBERAR_DESENV_P,
           NM_USUARIO        = nm_usuario_s,
           DT_ATUALIZACAO    = dt_atual_s
    WHERE  NR_SEQUENCIA      = NR_SEQUENCIA_P;

    CALL generate_latam_log(nr_sequencia_p, ie_liberar_desenv_old_w, CASE WHEN IE_LIBERAR_DESENV_P = 'K' THEN 1060572
                                                                     WHEN IE_LIBERAR_DESENV_P = 'S' THEN 1063902
                                                                ELSE 1060574
                                                                END);

    COMMIT;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
