CREATE OR REPLACE PROCEDURE encerar_cig_setor_pac ( nr_atendimento_p bigint, cd_setor_atendimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

nr_cig_atend_w		bigint;
nr_seq_glicemia_w	bigint;
cd_procedimento_w	bigint;
dt_horario_w		timestamp;
nr_prescricao_w		bigint;
nr_seq_horario_w	bigint;
nr_seq_proced_w		bigint;
nr_Seq_proc_interno_w	bigint;
c01 CURSOR FOR
SELECT	e.nr_cig_atend, 
	d.nr_sequencia, 
	a.nr_prescricao, 
	a.nr_sequencia 
from	proc_interno c, 
	atendimento_cig e, 
	atend_glicemia d, 
	prescr_medica b, 
	prescr_procedimento a 
where	a.nr_prescricao		= b.nr_prescricao 
and 	a.nr_seq_proc_interno	= c.nr_sequencia 
and 	d.nr_prescricao		= a.nr_prescricao 
and 	d.nr_seq_procedimento	= a.nr_sequencia 
and	e.nr_seq_glicemia	= d.nr_sequencia 
and	b.nr_atendimento 	= nr_atendimento_p 
and 	a.cd_setor_atendimento	= cd_setor_atendimento_p 
and 	c.ie_ctrl_glic		= 'CIG' 
and	d.ie_status_glic	in ('N','R','I') 
and	coalesce(a.ie_suspenso,'N')	<> 'S';

c02 CURSOR FOR 
SELECT	a.nr_sequencia, 
	b.cd_procedimento, 
	a.dt_horario, 
	a.nr_seq_proc_interno 
from	prescr_proc_hor a, 
	prescr_procedimento b 
where	a.nr_seq_procedimento	= b.nr_sequencia 
and 	a.nr_prescricao		= b.nr_prescricao 
and	b.nr_sequencia		= nr_seq_proced_w 
and 	b.nr_prescricao		= nr_prescricao_w 
and 	coalesce(a.dt_fim_horario::text, '') = '' 
and	coalesce(a.dt_suspensao::text, '') = '';
	

BEGIN 
open C01;
loop 
fetch C01 into	 
	nr_cig_atend_w, 
	nr_seq_glicemia_w, 
	nr_prescricao_w, 
	nr_seq_proced_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	--Encerrar o CIG 
	CALL alterar_status_cig(nr_Atendimento_p, nr_seq_glicemia_w, nr_cig_atend_w, 'T', nm_usuario_p);
	 
	open C02;
	loop 
	fetch C02 into	 
		nr_seq_horario_w, 
		cd_procedimento_w, 
		dt_horario_w, 
		nr_Seq_proc_interno_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin 
		-- suspender hor√°rios 
		CALL suspender_prescr_proc_hor( nr_seq_horario_w , nm_usuario_p);
		-- gerar evento ADEP 
		insert into prescr_mat_alteracao( 
				nr_sequencia, 
				dt_atualizacao, 
				nm_usuario, 
				dt_atualizacao_nrec, 
				nm_usuario_nrec, 
				nr_prescricao, 
				nr_seq_procedimento, 
				nr_seq_horario_proc, 
				dt_alteracao, 
				cd_pessoa_fisica, 
				ie_alteracao, 
				ds_justificativa, 
				ie_tipo_item, 
				dt_horario, 
				nr_atendimento, 
				cd_item, 
				nr_Seq_proc_interno, 
				DS_OBSERVACAO 
				) 
			values ( 
				nextval('prescr_mat_alteracao_seq'), 
				clock_timestamp(), 
				nm_usuario_p, 
				clock_timestamp(), 
				nm_usuario_p, 
				nr_prescricao_w, 
				nr_seq_proced_w, 
				nr_seq_horario_w, 
				clock_timestamp(), 
				obter_dados_usuario_opcao(nm_usuario_p,'C'), 
				12, 
				null, 
				'C', 
				dt_horario_w, 
				nr_atendimento_p, 
				cd_procedimento_w, 
				nr_Seq_proc_interno_w, 
				wheb_mensagem_pck.get_texto(795546));	
		end;
	end loop;
	close C02;
		 
end loop;
close C01;
 
if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
