CREATE OR REPLACE PROCEDURE encerrar_boletim_ocorrencia ( dt_encerramento_p timestamp, nr_seq_resp_bo_p bigint, nm_usuario_p text) AS $body$
DECLARE

				 
nr_seq_atend_pls_w		bigint;
nr_seq_evento_atend_w		bigint;


BEGIN 
 
update	sac_resp_bol_ocor 
set	dt_encerramento = dt_encerramento_p 
where	nr_sequencia	= nr_seq_resp_bo_p;
 
begin	 
select	b.nr_atend_pls, 
	b.nr_seq_evento_atend 
into STRICT	nr_seq_atend_pls_w, 
	nr_seq_evento_atend_w 
from	sac_resp_bol_ocor	a, 
	sac_boletim_ocorrencia	b 
where	a.nr_sequencia	= nr_seq_resp_bo_p 
and	b.nr_sequencia	= a.nr_seq_bo;
exception 
when others then 
	nr_seq_atend_pls_w := null;
end;
 
if (nr_seq_atend_pls_w IS NOT NULL AND nr_seq_atend_pls_w::text <> '') then 
	CALL pls_finalizar_atendimento(	nr_seq_atend_pls_w, 
					nr_seq_evento_atend_w, 
					null, 
					null, 
					nm_usuario_p);
end if;
 
commit;
 
end;
