SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE encerrar_ccg_vencido ( nr_atendimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_prescricao_w		bigint;
nr_seq_procedimento_w	bigint;
nr_seq_prot_glic_w	bigint;
nr_sequencia_w		bigint;

c01 CURSOR FOR
SELECT	a.nr_prescricao,
	b.nr_seq_procedimento,
	b.nr_seq_prot_glic,
	c.nr_sequencia
from	prescr_medica a,
	atend_glicemia b,
	atendimento_glicemia c
where	c.nr_seq_glicemia = b.nr_sequencia
and	c.nr_seq_protocolo = b.nr_seq_prot_glic
and	c.nr_atendimento = b.nr_atendimento
and	c.nr_atendimento = a.nr_atendimento
and	a.nr_prescricao = b.nr_prescricao
and	a.dt_validade_prescr < clock_timestamp()
and	coalesce(ie_status_glic, 'X') not in ('T','S')
and	a.nr_atendimento = nr_atendimento_p;


BEGIN

open c01;
loop
fetch c01 into
	nr_prescricao_w,
	nr_seq_procedimento_w,
	nr_seq_prot_glic_w,
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	update	atend_glicemia
	set	ie_status_glic = 'T',
		nm_usuario = nm_usuario_p
	where	nr_prescricao = nr_prescricao_w
	and	nr_seq_procedimento = nr_seq_procedimento_w
	and	nr_seq_prot_glic = nr_seq_prot_glic_w;
	--728884 = Encerramento automatico
	CALL gerar_alteracao_glicemia(nr_sequencia_w, 4, OBTER_DESC_EXPRESSAO(728884) , nm_usuario_p);

	end;
end loop;
close C01;

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
