SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE encerrar_ivc_vencido (nr_atendimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w 		bigint;

c01 CURSOR FOR
	SELECT	b.nr_sequencia
	from	prescr_medica a,
		adep_irrigacao b
	where	a.nr_prescricao 	= b.nr_prescricao
	and	a.nr_atendimento	= nr_atendimento_p
	and	a.dt_validade_prescr	< clock_timestamp()
	and	coalesce(ie_status_ivc,'X')	not in ('T','S')
	and	coalesce(b.dt_fim::text, '') = '';


BEGIN

OPEN c01;
LOOP
FETCH c01 INTO
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	update	adep_irrigacao
	set	dt_fim		= clock_timestamp(),
		ie_status_ivc	= 'T',
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	nr_sequencia	= nr_sequencia_w;

end loop;
close C01;

if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
