CREATE OR REPLACE PROCEDURE encerrar_qua_nao_conformidade ( ie_exige_motivo_encerr_p text, nr_seq_motivo_fechamento_p bigint, nr_sequencia_p bigint, nm_usuario_p text) AS $body$
BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') and (coalesce(ie_exige_motivo_encerr_p,'N') = 'N') then
	begin
	update	qua_nao_conformidade
	set		dt_encerramento	= clock_timestamp(),
			ie_status	= 'Encerrada',
			nm_usuario	= nm_usuario_p,
			dt_atualizacao	= clock_timestamp()
	where	nr_sequencia	= nr_sequencia_p;
	end;
elsif (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') and (coalesce(ie_exige_motivo_encerr_p,'N') = 'S') then
	begin
	update	qua_nao_conformidade
	set		dt_encerramento = clock_timestamp(),
			ie_status = 'Encerrada',
			nr_seq_motivo_encer = nr_seq_motivo_fechamento_p,
			nm_usuario = nm_usuario,
			dt_atualizacao = clock_timestamp()
	where	nr_sequencia = nr_sequencia_p;
	end;
end if;

commit;

end;
$body$
