SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE encerrar_risco_projeto ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_historico_w	bigint;


BEGIN


select	count(*)
into STRICT	qt_historico_w
from	proj_risco_hist
where	nr_seq_risco = nr_sequencia_p
and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');

if (qt_historico_w = 0 ) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(241431);
end if;

update	proj_risco_implantacao
set	dt_fim_real		= clock_timestamp(),
	nm_usuario_encerramento	= nm_usuario_p
where  	nr_sequencia		= nr_sequencia_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
