CREATE OR REPLACE PROCEDURE encerra_ordem_vinculada ( nr_seq_ordem_p bigint, nr_seq_estagio_p bigint, nm_usuario_p text) AS $body$
BEGIN
if (nr_seq_ordem_p IS NOT NULL AND nr_seq_ordem_p::text <> '') then
	update  man_ordem_servico x
	set     x.ie_status_ordem = 3,
			x.dt_atualizacao  = clock_timestamp(),
			x.nm_usuario      = nm_usuario_p,
			x.dt_inicio_real  = coalesce(dt_inicio_real,dt_ordem_servico),
			x.dt_fim_real     = clock_timestamp(),
			x.nr_seq_estagio  = CASE WHEN nr_seq_estagio_p=0 THEN nr_seq_estagio  ELSE nr_seq_estagio_p END
	where   x.nr_sequencia  in (  	SELECT	b.nr_sequencia
				from    	man_ordem_servico b,
					man_ordem_serv_vinc a
				where   	b.nr_sequencia = a.nr_seq_os_vinculada
				and     	b.ie_status_ordem <> '3'
				and     	coalesce(b.dt_fim_real::text, '') = ''
				and     	coalesce(b.nr_seq_wheb::text, '') = ''
				and     a.nr_seq_ordem_servico = nr_seq_ordem_p)
	and     coalesce(x.nr_seq_wheb::text, '') = '';
	commit;
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
