CREATE OR REPLACE PROCEDURE enc_os_nec_cor_proj_migr_tec ( nr_seq_os_corr_p bigint, nm_usuario_p text, nr_seq_os_tecnologia_p INOUT bigint) AS $body$
DECLARE


nr_seq_projeto_w	bigint;
nr_seq_os_enc_tec_w	bigint;


BEGIN
if (nr_seq_os_corr_p IS NOT NULL AND nr_seq_os_corr_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	select	po.nr_seq_proj
	into STRICT	nr_seq_projeto_w
	from	proj_ordem_servico po
	where	po.nr_seq_ordem = nr_seq_os_corr_p;

	if (nr_seq_projeto_w IS NOT NULL AND nr_seq_projeto_w::text <> '') then
		begin
		select	nextval('man_ordem_servico_seq')
		into STRICT	nr_seq_os_enc_tec_w
		;

		insert into man_ordem_servico(
			nr_sequencia,
			dt_ordem_servico,
			cd_pessoa_solicitante,
			nr_seq_localizacao,
			nr_seq_equipamento,
			ds_dano_breve,
			ie_prioridade,
			ds_dano,
			cd_funcao,
			nr_seq_grupo_des,
			ie_classificacao,
			ie_tipo_ordem,
			ie_status_ordem,
			dt_inicio_previsto,
			dt_fim_previsto,
			dt_inicio_desejado,
			dt_conclusao_desejada,
			nr_seq_estagio,
			nr_grupo_trabalho,
			nr_grupo_planej,
			nm_usuario,
			dt_atualizacao,
			ie_parado,
			ie_obriga_news,
			nr_seq_origem,
			ie_exclusiva,
			nm_usuario_nrec,
			dt_atualizacao_nrec,
			ie_origem_os,
			nr_seq_complex,
			nr_seq_classif,
			nr_seq_nivel_valor)
		SELECT	nr_seq_os_enc_tec_w,
			clock_timestamp(),
			cd_pessoa_solicitante,
			1272,
			41,
			substr('ENC: ' || ds_dano_breve,1,80),
			'A',
			ds_dano,
			cd_funcao,
			32,
			ie_classificacao,
			ie_tipo_ordem,
			'2',
			clock_timestamp(),
			clock_timestamp() + interval '7 days',
			clock_timestamp(),
			clock_timestamp() + interval '7 days',
			131,
			2,
			41,
			nm_usuario_p,
			clock_timestamp(),
			'N',
			'N',
			nr_seq_os_corr_p,
			'M',
			nm_usuario_p,
			clock_timestamp(),
			'1',
			null,
			58,
			2
		from	man_ordem_servico
		where	nr_sequencia = nr_seq_os_corr_p;

		insert into man_ordem_serv_arq(
			nr_sequencia,
			nr_seq_ordem,
			dt_atualizacao,
			nm_usuario,
			ds_arquivo,
			ie_anexar_email,
			ie_status_anexo,
			dt_atualizacao_nrec,
			nm_usuario_nrec)
		SELECT	nextval('man_ordem_serv_arq_seq'),
			nr_seq_os_enc_tec_w,
			clock_timestamp(),
			nm_usuario_p,
			ds_arquivo,
			ie_anexar_email,
			ie_status_anexo,
			clock_timestamp(),
			nm_usuario_p
		from	man_ordem_serv_arq
		where	nr_seq_ordem = nr_seq_os_corr_p;

		delete
		from	man_ordem_servico_exec
		where	nr_seq_ordem = nr_seq_os_enc_tec_w;

		delete
		from	man_ordem_ativ_prev
		where	nr_seq_ordem_serv = nr_seq_os_enc_tec_w;

		insert into man_ordem_servico_exec(
			nr_sequencia,
			nr_seq_ordem,
			dt_atualizacao,
			nm_usuario,
			nm_usuario_exec,
			qt_min_prev,
			dt_ult_visao,
			nr_seq_funcao,
			dt_recebimento,
			nr_seq_tipo_exec)
		values (
			nextval('man_ordem_servico_exec_seq'),
			nr_seq_os_enc_tec_w,
			clock_timestamp(),
			nm_usuario_p,
			'epmallmann',
			null,
			null,
			null,
			null,
			1);

		insert into proj_ordem_servico(
			nr_sequencia,
			nr_seq_proj,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_seq_ordem,
			ie_tipo_ordem)
		values (
			nextval('proj_ordem_servico_seq'),
			nr_seq_projeto_w,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_os_enc_tec_w,
			'CET');

		/*update	w_plan_teste_migr_proj
		set	ie_dependencia_tecnologia = 'S'
		where	nr_seq_projeto = nr_seq_projeto_w;*/
		end;
	end if;
	end;
end if;
nr_seq_os_tecnologia_p := nr_seq_os_enc_tec_w;
commit;
end;
