SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE enviar_ci_alter_mat ( cd_material_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


ds_titulo_w			varchar(255);
ds_comunicado_w			varchar(4000);
ds_material_w			varchar(4000);
cd_perfil_destino_w		varchar(4000);
cd_perfil_w			varchar(4000);
nr_sequencia_w			bigint;
nr_sequencia_ww			bigint;
nr_seq_comunic_w		bigint;
nr_min_prescr_w			bigint;
cd_material_w			bigint;
cont_w				bigint;
dt_cadastramento_w		timestamp;
dt_atualizacao_w		timestamp;

c01 CURSOR FOR
SELECT	a.ds_titulo,
	a.nr_sequencia
from	regra_ci_material a
where	coalesce(a.ie_situacao,'A') = 'A';

c02 CURSOR FOR
SELECT	b.cd_perfil
from	regra_ci_material_perfil b
where	b.nr_seq_regra	= nr_sequencia_ww;


BEGIN

select	min(nr_sequencia)
into STRICT	nr_sequencia_w
from	comunic_interna_classif
where	ie_tipo	= 'F';

select	count(*)
into STRICT	cont_w
from	protocolo_medic_material
where	cd_material	= cd_material_p;

if (cont_w > 0) then

	open C01;
	loop
	fetch C01 into
		ds_titulo_w,
		nr_sequencia_ww;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		ds_comunicado_w := null;

		select	Obter_desc_material(cd_material_p),
			dt_cadastramento,
			dt_atualizacao
		into STRICT	ds_material_w,
			dt_cadastramento_w,
			dt_atualizacao_w
		from	material a
		where	cd_material	= cd_material_p;

		cd_perfil_w := null;

		open c02;
		loop
		fetch c02 into
			cd_perfil_destino_w;
		EXIT WHEN NOT FOUND; /* apply on c02 */

			if (cd_perfil_w IS NOT NULL AND cd_perfil_w::text <> '') or (cd_perfil_w <> '') then
				cd_perfil_w := cd_perfil_w || ',';
			end if;

			cd_perfil_w := substr(cd_perfil_w || cd_perfil_destino_w,1,1000);

		end loop;
		close c02;

		if (cd_perfil_w IS NOT NULL AND cd_perfil_w::text <> '') and (substr(cd_perfil_w,length(cd_perfil_w), 1) <> ',') then
			cd_perfil_w	:= cd_perfil_w || ',';
		end if;

		ds_comunicado_w	:=	wheb_mensagem_pck.get_texto(312410,
						'DS_MATERIAL_W='|| DS_MATERIAL_W ||
						';DT_CADASTRAMENTO_W='|| DT_CADASTRAMENTO_W ||
						';DT_ATUALIZACAO_W='|| DT_ATUALIZACAO_W);
					/*Alterado material para não ser mais prescrevível, ou foi inativado.
					Material: #@DS_MATERIAL_W#@
					Dt Cadastramento: #@DT_CADASTRAMENTO_W#@
					Dt Atualização: #@DT_ATUALIZACAO_W#@*/
		if (ds_titulo_w IS NOT NULL AND ds_titulo_w::text <> '') then

			insert	into comunic_interna(
				dt_comunicado,
				ds_titulo,
				ds_comunicado,
				nm_usuario,
				--nm_usuario_destino,
				dt_atualizacao,
				ie_geral,
				ie_gerencial,
				ds_perfil_adicional,
				nr_sequencia,
				nr_seq_classif,
				dt_liberacao)
			values (
				clock_timestamp(),
				ds_titulo_w,
				ds_comunicado_w,
				nm_usuario_p,
				--null,
				clock_timestamp(),
				'N',
				'N',
				cd_perfil_w,
				nextval('comunic_interna_seq'),
				nr_sequencia_w,
				clock_timestamp());
			commit;
		end if;

	end loop;
	close C01;
end if;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE enviar_ci_alter_mat ( cd_material_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) FROM PUBLIC;

