SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE enviar_ci_aprov_cron ( nr_cronograma_p bigint, ie_opcao_retorno_p text) AS $body$
DECLARE


/*	IE_OPCAO_RETORNO_P:

	A: Aprovação de cronograma;
	C: Cancelamento de projeto.

*/
ds_titulo_w		comunic_interna.ds_titulo%type;
ds_mensagem_w   	comunic_interna.ds_comunicado%type;
nm_usuario_w    	comunic_interna.nm_usuario%type;
nr_sequencia_w  	comunic_interna.nr_sequencia%type;
cd_programador_w	proj_cron_etapa_equipe.cd_programador%type;
nr_ordem_servico_w	man_ordem_servico.nr_sequencia%type;
nr_seq_projeto_w	proj_projeto.nr_Sequencia%type;
ds_projeto_w		proj_projeto.ds_titulo%type;
ds_lista_os_w		varchar(10000);
ds_motivo_w		varchar(4000);

c01 CURSOR FOR
	SELECT  	distinct a.cd_programador,
		obter_usuario_pf_ci(a.cd_programador)
	from	proj_cron_etapa_equipe a,
		proj_cron_etapa b
	where	a.nr_seq_etapa_cron = b.nr_sequencia
	and	b.nr_seq_cronograma = nr_cronograma_p
	order by 1;

C02 CURSOR FOR
	SELECT	a.nr_sequencia
	from	man_ordem_servico a,
		proj_cron_etapa_equipe b,
		proj_cron_etapa c
	where	a.nr_seq_proj_cron_etapa  = c.nr_sequencia
	and	b.nr_seq_etapa_cron = c.nr_sequencia
	and	b.cd_programador = cd_programador_w
	and	c.nr_seq_cronograma = nr_cronograma_p
	order by 1 desc;


BEGIN

select	max(b.nr_sequencia),
	max(b.ds_titulo)
into STRICT	nr_seq_projeto_w,
	ds_projeto_w
from	proj_cronograma a,
	proj_projeto b
where	a.nr_seq_proj = b.nr_sequencia
and	a.nr_sequencia = nr_cronograma_p;

if (ie_opcao_retorno_p = 'C') then
	ds_titulo_w		:= wheb_mensagem_pck.get_texto(320341,
					'NR_SEQ_PROJ=' || nr_seq_projeto_w || ';' ||
					'DS_PROJETO=' || ds_projeto_w);		--Cancelamento do projeto #@NR_SEQ_PROJ#@ - #@DS_PROJETO#@.
else
	ds_titulo_w		:= wheb_mensagem_pck.get_texto(302513,
					'NR_PROJETO=' || nr_seq_projeto_w || ';' ||
					'DS_PROJETO=' || ds_projeto_w);
end if;

/*

PRP/NPI: #@NR_PROJETO#@ - #@DS_PROJETO#@

Foi aprovado o cronograma #@NR_CRONOGRAMA#@ do projeto #@NR_PROJETO#@ - #@DS_PROJETO#@.
Seguem OS's abertas para o mesmo: #@DS_LISTA_OS#@

*/
open C01;
loop
fetch C01 into
	cd_programador_w,
	nm_usuario_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	ds_lista_os_w := '';

	open C02;
	loop
	fetch C02 into
		nr_ordem_servico_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin

		ds_lista_os_w := ds_lista_os_w ||' '||nr_ordem_servico_w;

		end;
	end loop;
	close C02;

	if (ie_opcao_retorno_p = 'C') then
		begin

			select 	ds_historico
			into STRICT 	ds_motivo_w
			from	com_cliente_hist
			where	nr_seq_projeto = nr_seq_projeto_w
			and	nr_seq_tipo = 21;

			ds_mensagem_w 		:= wheb_mensagem_pck.get_texto(320340,
						  'NR_SEQ_PROJ=' || nr_seq_projeto_w || ';' ||
						  'DS_PROJETO=' || ds_projeto_w || ';' ||
						  'DS_MOTIVO=' || ds_motivo_w);		--Foi cancelado o projeto #@NR_SEQ_PROJ#@ - #@DS_PROJETO#@. Motivo: #@DS_MOTIVO#@. Todas as OS's vinculadas ao mesmo estão encerradas.
		end;
	else
		ds_mensagem_w		:= wheb_mensagem_pck.get_texto(302514,
					  'NR_CRONOGRAMA=' || nr_cronograma_p || ';' ||
					  'NR_PROJETO=' || nr_seq_projeto_w || ';' ||
					  'DS_PROJETO=' || ds_projeto_w || ';' ||
					  'DS_LISTA_OS=' || ds_lista_os_w);
	end if;

	select	nextval('comunic_interna_seq')
	into STRICT	nr_sequencia_w
	;

	insert into comunic_interna(dt_comunicado,
		ds_titulo,
		ds_comunicado,
		nm_usuario,
		dt_atualizacao,
		ie_geral,
		nm_usuario_destino,
		cd_perfil,
		nr_sequencia,
		ie_gerencial,
		nr_seq_classif,
		ds_perfil_adicional,
		cd_setor_destino,
		ds_setor_adicional,
		dt_liberacao,
		ds_grupo)
	values (clock_timestamp(),
		ds_titulo_w,
		ds_mensagem_w,
		'Tasy',
		clock_timestamp(),
		'N',
		nm_usuario_w,
		null,
		nr_sequencia_w,
		'N',
		null,
		null,
		null,
		' ',
		clock_timestamp(),
		null);


	end;
end loop;
close C01;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE enviar_ci_aprov_cron ( nr_cronograma_p bigint, ie_opcao_retorno_p text) FROM PUBLIC;

