SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE enviar_ci_baixar_req_motivo ( nr_requisicao_p bigint, cd_motivo_baixa_p bigint, nm_usuario_p text) AS $body$
DECLARE


ds_motivo_baixa_w				varchar(255);
nm_usuario_destino_w			varchar(15);
ds_titulo_w				varchar(80);
ds_comunicacao_w				varchar(2000);
nr_seq_classif_w				bigint;


BEGIN

select	substr(obter_desc_motivo_baixa_req(cd_motivo_baixa_p),1,80)
into STRICT	ds_motivo_baixa_w
;

select	obter_usuario_pessoa(cd_pessoa_requisitante)
into STRICT	nm_usuario_destino_w
from	requisicao_material
where	nr_requisicao = nr_requisicao_p;

ds_titulo_w		:= Wheb_mensagem_pck.get_Texto(300264); /*'Baixa da requisição por motivo';*/
ds_comunicacao_w	:= substr(WHEB_MENSAGEM_PCK.get_texto(300267,'NR_REQUISICAO_P='|| NR_REQUISICAO_P ||';NM_USUARIO_P='|| NM_USUARIO_P ||';DS_MOTIVO_BAIXA_W='|| DS_MOTIVO_BAIXA_W),1,2000);
			/*A requisição de materiais e medicamentos número #@NR_REQUISICAO_P#@, foi baixada pelo usuário #@NM_USUARIO_P#@.
			Motivo: #@DS_MOTIVO_BAIXA_W#@.*/
if (nm_usuario_destino_w IS NOT NULL AND nm_usuario_destino_w::text <> '') then

	select	max(obter_classif_comunic('F'))
	into STRICT	nr_seq_classif_w
	;

	CALL Gerar_Comunic_Padrao(clock_timestamp(),ds_titulo_w,ds_comunicacao_w,nm_usuario_p,'N',nm_usuario_destino_w,'N',nr_seq_classif_w,'','','',clock_timestamp(),'','');
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
