CREATE OR REPLACE PROCEDURE enviar_ci_carta_medica ( cd_pessoa_fisica_p text, cd_pessoa_usuario_p text, cd_pessoa_destino_p text, nm_usuario_p text) AS $body$
DECLARE

 
ds_titulo_w				varchar(255);
nm_paciente_w			varchar(255);
nm_pessoa_usuario_w		varchar(255);
ds_comunicado_w			varchar(4000);
nr_sequencia_w			bigint;
nm_usuario_destino_w	usuario.nm_usuario%type;


BEGIN 
 
select	min(nr_sequencia) 
into STRICT	nr_sequencia_w 
from	comunic_interna_classif 
where	ie_tipo	= 'F';
 
nm_paciente_w		:= substr(obter_nome_pf(cd_pessoa_fisica_p),1,255);
nm_pessoa_usuario_w	:= substr(obter_nome_pf(cd_pessoa_usuario_p),1,255);
 
ds_comunicado_w := 	substr(nm_pessoa_usuario_w || ' ' || obter_desc_expressao(802166) ||' '|| nm_paciente_w ||' '|| obter_desc_expressao(802168) ||'.',1,4000);
 
select	max(nm_usuario) 
into STRICT	nm_usuario_destino_w 
from	usuario 
where	cd_pessoa_fisica = cd_pessoa_destino_p 
and		ie_situacao = 'A';
 
if (nm_usuario_destino_w IS NOT NULL AND nm_usuario_destino_w::text <> '') then 
 
	insert	into comunic_interna(	 
			dt_comunicado, 
			ds_titulo, 
			ds_comunicado, 
			nm_usuario, 
			nm_usuario_destino, 
			dt_atualizacao, 
			ie_geral, 
			ie_gerencial, 
			ds_perfil_adicional, 
			nr_sequencia, 
			nr_seq_classif, 
			dt_liberacao) 
		values ( 
			clock_timestamp(), 
			obter_desc_expressao(787095)||': '||nm_paciente_w, 
			ds_comunicado_w, 
			nm_usuario_p, 
			nm_usuario_destino_w, 
			clock_timestamp(), 
			'N', 
			'N', 
			null, 
			nextval('comunic_interna_seq'), 
			nr_sequencia_w, 
			clock_timestamp());	
 
	commit;				
	 
end if;
	 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
