SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE enviar_ci_internacao_sus ( nm_usuario_p text) AS $body$
DECLARE


cd_perfil_w		integer;
ds_lista_perfil_w	varchar(4000);
ds_titulo_w		varchar(255);
ds_lista_usuario_destino_w	varchar(255);
ds_comunicado_w		varchar(4000);
nm_usuario_destino_w	varchar(4000);
count_w			bigint;
nr_seq_w		bigint;

c01 CURSOR FOR
SELECT	ds_titulo,
	substr(ds_comunicado,1,4000),
	nr_sequencia
from	envio_ci_internacao_sus;

c02 CURSOR FOR
SELECT	a.cd_perfil
from	envio_ci_intern_sus_perfil a
where	a.nr_seq_regra		= nr_seq_w;

c15 CURSOR FOR
SELECT	b.nm_usuario_regra
from	envio_ci_intern_sus_usu b
where	b.nr_seq_regra	= nr_seq_w;


BEGIN

select	count(*)
into STRICT	count_w
from	envio_ci_internacao_sus;

if (count_w > 0) then

	open C01;
	loop
	fetch C01 into
		ds_titulo_w,
		ds_comunicado_w,
		nr_seq_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		open c02;
		loop
		fetch c02 into
			cd_perfil_w;
		EXIT WHEN NOT FOUND; /* apply on c02 */
			begin

			if (ds_lista_perfil_w IS NOT NULL AND ds_lista_perfil_w::text <> '') then
				ds_lista_perfil_w := substr(ds_lista_perfil_w || ', ',1,4000);
			end if;

			ds_lista_perfil_w	:= substr(ds_lista_perfil_w || cd_perfil_w,1,4000);

			end;
		end loop;
		close c02;

		open C15;
		loop
		fetch C15 into
			nm_usuario_destino_w;
		EXIT WHEN NOT FOUND; /* apply on C15 */
			if (nm_usuario_destino_w <> nm_usuario_p) then
				if (ds_lista_usuario_destino_w IS NOT NULL AND ds_lista_usuario_destino_w::text <> '') then
					ds_lista_usuario_destino_w := substr(ds_lista_usuario_destino_w || ',',1,1000);
				end if;

				ds_lista_usuario_destino_w := substr(ds_lista_usuario_destino_w || nm_usuario_destino_w,1,1000);
			end if;
		end loop;
		close C15;

		if (ds_lista_perfil_w IS NOT NULL AND ds_lista_perfil_w::text <> '') or (ds_lista_usuario_destino_w IS NOT NULL AND ds_lista_usuario_destino_w::text <> '') then

			insert  into comunic_interna(
				dt_comunicado,
				ds_titulo,
				ds_comunicado,
				nm_usuario,
				dt_atualizacao,
				nr_sequencia,
				ie_gerencial,
				dt_liberacao,
				ds_perfil_adicional,
				nm_usuario_destino)
			values (clock_timestamp(),
				ds_titulo_w,
				ds_comunicado_w,
				nm_usuario_p,
				clock_timestamp(),
				nextval('comunic_interna_seq'),
				'N',
				clock_timestamp(),
				ds_lista_perfil_w,
				ds_lista_usuario_destino_w);
		end if;

		if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

		end;
	end loop;
	close C01;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
