CREATE OR REPLACE PROCEDURE enviar_ci_nova_aval_mat ( nr_sequencia_p text, nm_usuario_p text) AS $body$
DECLARE

 
cd_material_w			bigint;
nr_sequencia_w			bigint;
ds_material_w			varchar(255);
cd_pessoa_resp_w		varchar(10);
nm_usuario_dest_w		varchar(15);
dt_inicio_aval_w		timestamp;
cd_cgc_distrib_w		varchar(14);
ds_distribuidor_w		varchar(255);
nr_seq_classif_w		bigint;

 

BEGIN 
 
select	cd_material, 
	substr(obter_dados_material(cd_material,'D'),1,255), 
	cd_pessoa_resp, 
	dt_inicio_avaliacao, 
	cd_cgc_distribuidor, 
	obter_nome_pf_pj(null,cd_cgc_distribuidor) 
into STRICT	cd_material_w, 
	ds_material_w, 
	cd_pessoa_resp_w, 
	dt_inicio_aval_w, 
	cd_cgc_distrib_w, 
	ds_distribuidor_w 
from	mat_avaliacao 
where	nr_sequencia = nr_sequencia_p;
 
if (cd_pessoa_resp_w IS NOT NULL AND cd_pessoa_resp_w::text <> '') then 
 
	select	max(nm_usuario) 
	into STRICT	nm_usuario_dest_w 
	from	usuario 
	where	cd_pessoa_fisica = cd_pessoa_resp_w;
 
	if (nm_usuario_dest_w IS NOT NULL AND nm_usuario_dest_w::text <> '') then 
 
	select	nextval('comunic_interna_seq') 
	into STRICT	nr_sequencia_w 
	;	
 
	select	obter_classif_comunic('F') 
	into STRICT	nr_seq_classif_w 
	;
 
	insert into comunic_interna( 
		dt_comunicado, 
		ds_titulo, 
		ds_comunicado, 
		nm_usuario, 
		dt_atualizacao, 
		ie_geral, 
		nm_usuario_destino, 
		nr_sequencia, 
		ie_gerencial, 
		nr_seq_classif, 
		dt_liberacao) 
	values (	clock_timestamp(), 
		wheb_mensagem_pck.get_Texto(312414), /*'Nova avaliação de material disponível.',*/
 
		wheb_mensagem_pck.get_texto(312415, 
			'CD_MATERIAL_W='|| CD_MATERIAL_W || 
			';DS_MATERIAL_W='|| DS_MATERIAL_W || 
			';DT_INICIO_AVAL_W='|| DT_INICIO_AVAL_W || 
			';CD_CGC_DISTRIB_W='|| CD_CGC_DISTRIB_W || 
			';DS_DISTRIBUIDOR_W='|| DS_DISTRIBUIDOR_W), 
			/*Um novo registro na função Gestão da Avaliação de Produtos esta disponível para liberação. 
			Dados do registro: 
			Material: #@CD_MATERIAL_W#@ - #@DS_MATERIAL_W#@. 
			Início da avaliação: #@DT_INICIO_AVAL_W#@ 
			Distribuidor: #@CD_CGC_DISTRIB_W#@ - #@DS_DISTRIBUIDOR_W#@.*/
 
		nm_usuario_p, 
		clock_timestamp(), 
		'N', 
		nm_usuario_dest_w, 
		nr_sequencia_w, 
		'N', 
		nr_seq_classif_w, 
		clock_timestamp());
	end if;
end if;	
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
