CREATE OR REPLACE PROCEDURE enviar_ci_novo_usuario ( funcoes_novo_funcionario_p text, nm_usuario_p text, ds_pessoa_fisica_p text, cd_estabelecimento_p bigint, tp_acesso_p text) AS $body$
DECLARE

 
ds_titulo_w			varchar(200);
ds_mensagem_w  		varchar(4000);
nm_usuario_w  		varchar(15);
ds_pessoa_fisica_w		varchar(120);
nr_sequencia_w 		bigint;
cd_estabelecimento_w		bigint;
nm_usuario_lib_w		varchar(50);
ds_usuario_w			varchar(10000);

c01 CURSOR FOR 
	SELECT distinct a.nm_usuario 
	from	usuario_perfil	a, 
		funcao_perfil	d 
	where 	d.cd_perfil	= a.cd_perfil 
	and 	d.cd_funcao	= 6001;


BEGIN 
ds_pessoa_fisica_w	:= ds_pessoa_fisica_p;
nm_usuario_w		:= nm_usuario_p;
ds_titulo_w		:= wheb_mensagem_pck.get_texto(298060, 
							'IE_TIPO_ACESSO=' || tp_acesso_p || ';' || 
							'NM_PESSOA=' || obter_nome_pf(ds_pessoa_fisica_w));
ds_mensagem_w		:= wheb_mensagem_pck.get_texto(298069, 
							'NM_PESSOA=' || obter_nome_pf(ds_pessoa_fisica_w) || ';' || 
							'DS_FUNCAO=' || funcoes_novo_funcionario_p);
 
open C01;
loop 
fetch C01 into 
	nm_usuario_lib_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	ds_usuario_w	:= ds_usuario_w || nm_usuario_lib_w || ',';
 
	if (length(ds_usuario_w) >= 3800) then 
		begin 
 
		select	nextval('comunic_interna_seq') 
		into STRICT	nr_sequencia_w 
		;
 
		insert into comunic_interna(dt_comunicado, 
			ds_titulo, 
			ds_comunicado, 
			nm_usuario, 
			dt_atualizacao, 
			ie_geral, 
			nm_usuario_destino, 
			cd_perfil, 
			nr_sequencia, 
			ie_gerencial, 
			nr_seq_classif, 
			ds_perfil_adicional, 
			cd_setor_destino, 
			cd_estab_destino, 
			ds_setor_adicional, 
			dt_liberacao, 
			ds_grupo) 
		values (clock_timestamp(), 
			ds_titulo_w, 
			ds_mensagem_w, 
			nm_usuario_w, 
			clock_timestamp(), 
			'N', 
			ds_usuario_w, 
			null, 
			nr_sequencia_w, 
			'N', 
			null, 
			null, 
			null, 
			cd_estabelecimento_w, 
			' ', 
			clock_timestamp(), 
			null);
 
		ds_usuario_w	:= '';
 
		end;
	end if;
 
	end;
end loop;
close C01;
 
if (ds_usuario_w <> '') then 
	begin 
 
	select	nextval('comunic_interna_seq') 
	into STRICT	nr_sequencia_w 
	;
 
	insert into comunic_interna(dt_comunicado, 
		ds_titulo, 
		ds_comunicado, 
		nm_usuario, 
		dt_atualizacao, 
		ie_geral, 
		nm_usuario_destino, 
		cd_perfil, 
		nr_sequencia, 
		ie_gerencial, 
		nr_seq_classif, 
		ds_perfil_adicional, 
		cd_setor_destino, 
		cd_estab_destino, 
		ds_setor_adicional, 
		dt_liberacao, 
		ds_grupo) 
	values (clock_timestamp(), 
		ds_titulo_w, 
		ds_mensagem_w, 
		nm_usuario_w, 
		clock_timestamp(), 
		'N', 
		ds_usuario_w, 
		null, 
		nr_sequencia_w, 
		'N', 
		null, 
		null, 
		null, 
		cd_estabelecimento_w, 
		' ', 
		clock_timestamp(), 
		null);
 
	end;
end if;
 
commit;
 
end;
$body$
