SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE enviar_ci_prescr_dieta ( nr_prescricao_p bigint, nm_usuario_p text, ie_momento_p text, cd_dieta_p bigint) AS $body$
DECLARE


ds_titulo_w			varchar(255);
nm_paciente_w			varchar(255);
nm_medico_w			varchar(255);
ds_comunicado_w			varchar(4000);
cd_perfil_w			bigint;
nr_sequencia_w			bigint;
cd_dieta_w			bigint;
nr_Seq_regra_w			bigint;
ds_perfil_adicional_w		varchar(4000) := '';
ds_unidade_w			varchar(255);

c01 CURSOR FOR
SELECT	distinct cd_dieta
from	prescr_dieta
where	nr_prescricao = nr_prescricao_p
and	((ie_momento_p	= 'L') or (cd_dieta	= cd_dieta_p));

c02 CURSOR FOR
SELECT	distinct ds_titulo,
		ds_comunicado,
		nr_sequencia
from	rep_regra_envio_ci_dieta
where	((cd_dieta	     =  cd_dieta_w) or (coalesce(cd_dieta::text, '') = ''))
and		coalesce(ie_momento,'L')  =  ie_momento_p;

c03 CURSOR FOR
SELECT	distinct cd_perfil
from	rep_regra_ci_perfil_dieta
where	nr_seq_regra	= nr_seq_regra_w;


BEGIN

select	min(nr_sequencia)
into STRICT	nr_sequencia_w
from	comunic_interna_classif
where	ie_tipo	= 'F';

select	max(obter_nome_pf_pj(cd_pessoa_fisica,null)),
		max(obter_nome_pf_pj(cd_prescritor,null)),
		max(Obter_Unidade_Atendimento(obter_atendimento_prescr(nr_prescricao_p), 'A', 'SAU'))
into STRICT	nm_paciente_w,
		nm_medico_w,
		ds_unidade_w
from	prescr_medica
where	nr_prescricao	= nr_prescricao_p;

open C01;
loop
fetch C01 into
	cd_dieta_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	open C02;
	loop
	fetch C02 into
		ds_titulo_w,
		ds_comunicado_w,
		nr_seq_regra_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin

		ds_comunicado_w := 	substr(ds_comunicado_w || chr(13) ||
									obter_desc_expressao(325814) || nr_prescricao_p || chr(13) ||
									obter_desc_expressao(343877) || nm_paciente_w || chr(13) ||
									obter_desc_expressao(309430) || ': ' || ds_unidade_w || chr(13) ||
									obter_desc_expressao(296217) || ': ' || nm_medico_w,1,4000);

		open C03;
		loop
		fetch C03 into
			cd_perfil_w;
		EXIT WHEN NOT FOUND; /* apply on C03 */
			begin
			ds_perfil_adicional_w := ds_perfil_adicional_w || cd_perfil_w ||',';
			end;
		end loop;
		close C03;


		insert	into comunic_interna(
				dt_comunicado,
				ds_titulo,
				ds_comunicado,
				nm_usuario,
				dt_atualizacao,
				ie_geral,
				ie_gerencial,
				ds_perfil_adicional,
				nr_sequencia,
				nr_seq_classif,
				dt_liberacao)
			values (
				clock_timestamp(),
				ds_titulo_w,
				ds_comunicado_w,
				nm_usuario_p,
				clock_timestamp(),
				'N',
				'N',
				ds_perfil_adicional_w,
				nextval('comunic_interna_seq'),
				nr_sequencia_w,
				clock_timestamp());

		end;
	end loop;
	close C02;

	end;
end loop;
close C01;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
