SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE enviar_comunic_agendada () AS $body$
DECLARE


dt_agendamento_w	timestamp;
nr_sequencia_w		bigint;

C01 CURSOR FOR
SELECT	nr_sequencia,
	dt_agendamento
from	comunic_interna
where	coalesce(dt_liberacao::text, '') = ''
and	(dt_agendamento IS NOT NULL AND dt_agendamento::text <> '')
and	dt_agendamento <= clock_timestamp();


BEGIN

open c01;
loop
fetch c01 into
	nr_sequencia_w,
	dt_agendamento_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	update comunic_interna set dt_liberacao = clock_timestamp() where nr_sequencia = nr_sequencia_w;
	end;
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE enviar_comunic_agendada () FROM PUBLIC;

