SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE enviar_comunic_aprov_exame ( nr_prescricao_p bigint, nr_seq_prescr_p bigint, dt_aprovacao_p timestamp, nm_usuario_p text) AS $body$
DECLARE


quebra_w			varchar(20) := chr(13)||chr(10);
ds_mensagem			varchar(500);
qt_dias_aviso_w			integer;
cd_medico_w			varchar(10);
nm_usuario_destino_w		varchar(60);
ie_gerar_medico_solic_w		varchar(1)			;
nr_atendimento_w		bigint;
nm_paciente_w			varchar(60);
dt_prescricao_w			timestamp;
dt_liberacao_w			timestamp;
ds_procedimento_w		procedimento.ds_procedimento%TYPE;
nm_medico_w			varchar(60);
nm_usuarios_destino_w		varchar(2000);
ds_mensagem_w			varchar(2000);

C01 CURSOR FOR
SELECT	cd_pessoa_fisica,
	substr(obter_usuario_pessoa(cd_pessoa_fisica),1,60) nm_usuario_destino,
	ie_gerar_medico_solic
from	regra_comunic_aprov_envio;

C02 CURSOR FOR
SELECT	a.nr_atendimento,
	substr(obter_nome_pf(a.cd_pessoa_fisica),1,100) nm_paciente,
	to_date(a.dt_prescricao,'dd/mm/yyyy hh24:mi:ss'),
	substr(obter_nome_medico(a.cd_medico,'NC'),1,100) nm_medico,
	to_date(a.dt_liberacao,'dd/mm/yyyy hh24:mi:ss'),
	substr(Obter_Desc_Prescr_Proc(b.CD_PROCEDIMENTO,b.IE_ORIGEM_PROCED, b.nr_seq_proc_interno),1,255) ds_procedimento
from	prescr_medica a,
	prescr_procedimento b
where	a.nr_prescricao = b.nr_prescricao
and	a.nr_prescricao = nr_prescricao_p	
and	b.nr_sequencia = nr_seq_prescr_p
and	ie_gerar_medico_solic_w = 'S'
and	a.cd_medico = cd_medico_w

union

SELECT	a.nr_atendimento,
	substr(obter_nome_pf(a.cd_pessoa_fisica),1,100) nm_paciente,
	to_date(a.dt_prescricao,'dd/mm/yyyy hh24:mi:ss'),
	substr(obter_nome_medico(a.cd_medico,'NC'),1,100) nm_medico,
	to_date(a.dt_liberacao,'dd/mm/yyyy hh24:mi:ss'),
	substr(Obter_Desc_Prescr_Proc(b.CD_PROCEDIMENTO,b.IE_ORIGEM_PROCED, b.nr_seq_proc_interno),1,255) ds_procedimento
from	prescr_medica a,
	prescr_procedimento b
where	a.nr_prescricao = b.nr_prescricao
and	a.nr_prescricao = nr_prescricao_p	
and	b.nr_sequencia = nr_seq_prescr_p
and	ie_gerar_medico_solic_w = 'N';


BEGIN

select	coalesce(max(qt_dias_aviso),0)
into STRICT	qt_dias_aviso_w
from	regra_gerar_comunic_aprov;

if (qt_dias_aviso_w <> 0) and (ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(dt_liberacao_w) - ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(dt_aprovacao_p) > qt_dias_aviso_w) then
	open C01;
	loop	
	fetch C01 into
		cd_medico_w,
		nm_usuario_destino_w,
		ie_gerar_medico_solic_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		
		open C02;
			loop
			fetch C02 into
				nr_atendimento_w,
				nm_paciente_w,
				dt_prescricao_w,
				nm_medico_w,
				dt_liberacao_w,
				ds_procedimento_w;
			EXIT WHEN NOT FOUND; /* apply on C02 */
			begin
			
			if (nm_usuario_destino_w IS NOT NULL AND nm_usuario_destino_w::text <> '') then
			nm_usuarios_destino_w := nm_usuarios_destino_w || nm_usuario_destino_w || ', ';
			end if;
		
			ds_mensagem_w  := obter_desc_expressao(775048)/*'Atendimento: '*/
 ||  nr_atendimento_w || quebra_w ||
				obter_desc_expressao(325811) /*'Paciente: '*/
 || nm_paciente_w || quebra_w ||
				'Data prescric?o: ' || dt_prescricao_w || quebra_w ||
				obter_desc_expressao(622367)/*'Medico: '*/
 || nm_medico_w || quebra_w ||
				obter_desc_expressao(621805)/*'Exame: '*/
 || ds_procedimento_w || quebra_w ||
				obter_desc_expressao(595449)/*'Data liberacao: '*/
 || dt_liberacao_w;
			
			end;
			end loop;
			close C02;
	end loop;
	close C01;

	CALL Gerar_Comunic_Padrao(clock_timestamp(),
				'Comunicac?o de aprovac?o de exames',
				ds_mensagem_w,
				nm_usuario_p,
				'N',
				nm_usuarios_destino_w,
				'N',
				null,
				null,
				null,
				null,
				clock_timestamp(),
				null,
				null);
end if;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE enviar_comunic_aprov_exame ( nr_prescricao_p bigint, nr_seq_prescr_p bigint, dt_aprovacao_p timestamp, nm_usuario_p text) FROM PUBLIC;

