CREATE OR REPLACE PROCEDURE enviar_comunic_conv ( nr_atendimento_p bigint, cd_pessoa_fisica_p text, cd_convenio_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

qt_idade_w			bigint;
C04 CURSOR FOR
	SELECT	a.nr_seq_evento 
	from	regra_envio_sms a 
	where	a.cd_estabelecimento	= cd_estabelecimento_p 
	and	a.ie_evento_disp	= 'TCE' 
	and	qt_idade_w between coalesce(qt_idade_min,0)	and coalesce(qt_idade_max,9999) 
	and (obter_se_convenio_rec_alerta(cd_convenio_p,nr_sequencia) = 'S') 
	AND 	obter_se_regra_envio(a.nr_sequencia, nr_atendimento_p) = 'S'	 
	and	coalesce(a.ie_situacao,'A') = 'A';

nr_seq_evento_w	bigint;

 

BEGIN 
 
begin 
qt_idade_w	:= coalesce(obter_idade_pf(cd_pessoa_fisica_p,clock_timestamp(),'A'),0);
exception 
	when 	others then 
		qt_idade_w := 0;
end;
 
open C04;
loop 
fetch C04 into	 
	nr_seq_evento_w;
EXIT WHEN NOT FOUND; /* apply on C04 */
	begin 
	CALL gerar_evento_paciente_trigger(nr_seq_evento_w,nr_atendimento_p,cd_pessoa_fisica_p,null,nm_usuario_p);
	end;
end loop;
close C04;
 
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
