SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE enviar_comunic_evento_trigger ( nr_sequencia_p bigint, nr_seq_evento_p bigint, ds_evento_p text, cd_pessoa_fisica_p text, nr_atendimento_p bigint, ie_classificacao_p text, cd_estabelecimento_p bigint, cd_setor_atendimento_p bigint, nm_usuario_p text, nr_seq_classif_evento_p text) AS $body$
DECLARE

			 
nr_seq_evento_w		bigint;
ds_evento_w		varchar(4000);
ds_mensagem_w		varchar(32000);
nm_paciente_w		varchar(250);
quebra_w			varchar(10) := chr(13)||chr(10);
ds_perfis_w		varchar(4000);
ds_setores_w		varchar(4000);
ds_usuarios_w		varchar(4000);
cd_perfil_w		bigint;
ds_titulo_w		varchar(255);
nr_atendimento_w		bigint;
ie_classificacao_w		varchar(10);
cd_empresa_w		bigint;
ds_setor_Atendimento_w	varchar(255);
cd_setor_atendimento_w	integer;
nm_usuario_destino_w	varchar(255);
ds_classif_evento_w	varchar(255);
ds_idade_w		varchar(10);
ds_grupo_w		varchar(255);
nr_seq_grupo_usu_w	bigint;
ds_evento_traducao_w	dic_expressao.ds_expressao_br%type;

C01 CURSOR FOR 
	SELECT	cd_perfil, 
		cd_setor_atendimento, 
		nm_usuario_destino, 
		nr_seq_grupo_usuario 
	from	qua_evento_comunic 
	where	nr_seq_evento	= nr_seq_evento_w 
	and	coalesce(cd_setor_evento,coalesce(cd_setor_atendimento_p,0)) = coalesce(cd_setor_atendimento_p,0);


BEGIN 
select	nr_seq_evento_p, 
	ds_evento_p, 
	obter_nome_pf(cd_pessoa_fisica_p),	 
	nr_atendimento_p, 
	ie_classificacao_p, 
	obter_empresa_estab(cd_estabelecimento_p), 
	obter_Nome_Setor(cd_setor_atendimento_p), 
	obter_desc_evento_classif(nr_seq_classif_evento_p), 
	Obter_Idade_PF(cd_pessoa_fisica_p,clock_timestamp(),'A') 
into STRICT	nr_seq_evento_w, 
	ds_evento_w, 
	nm_paciente_w, 
	nr_atendimento_w, 
	ie_classificacao_w, 
	cd_empresa_w, 
	ds_setor_Atendimento_w, 
	ds_classif_evento_w, 
	ds_idade_w
;
 
open C01;
loop 
fetch C01 into	 
	cd_perfil_w, 
	cd_setor_atendimento_w, 
	nm_usuario_destino_w, 
	nr_seq_grupo_usu_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	if (coalesce(cd_perfil_w,0) > 0) then 
		ds_perfis_w	:= substr(cd_perfil_w ||','|| ds_perfis_w,1,4000);
	end if;
	if (coalesce(cd_setor_atendimento_w,0) > 0) then 
		ds_setores_w	:= substr(cd_setor_atendimento_w ||','|| ds_setores_w,1,4000);
	end if;
	if (coalesce(nm_usuario_destino_w,'X') <> 'X') then 
		ds_usuarios_w	:= substr(nm_usuario_destino_w ||','|| ds_usuarios_w,1,4000);
	end if;
	if (coalesce(nr_seq_grupo_usu_w,0) > 0) then 
		ds_grupo_w	:= substr(nr_seq_grupo_usu_w ||','|| ds_grupo_w,1,255);
	end if;
	end;
end loop;
close C01;
 
select	substr(obter_desc_expressao(cd_exp_evento, ds_evento),1,255) 
into STRICT	ds_evento_traducao_w 
from	qua_evento 
where	nr_sequencia = nr_seq_evento_w;
 
ds_titulo_w	:= substr(wheb_mensagem_pck.get_texto(305624,'NM_PACIENTE='||nm_paciente_w),1,255);
ds_mensagem_w	:= wheb_mensagem_pck.get_texto(305633,	'NM_PACIENTE='		||nm_paciente_w		|| 
							';DS_IDADE='		||ds_idade_w		|| 
							';NR_ATENDIMENTO='	||nr_atendimento_w	|| 
							';DS_SETOR_ATENDIMENTO='||ds_setor_atendimento_w|| 
							';DS_EVENTO='||ds_evento_traducao_w);
 
if (ie_classificacao_w IS NOT NULL AND ie_classificacao_w::text <> '') then 
	ds_mensagem_w	:= ds_mensagem_w||quebra_w|| 
			wheb_mensagem_pck.get_texto(305642,'DS_CLASSIFICACAO='||obter_desc_classif_evento(ie_classificacao_w,cd_empresa_w));
end if;
if (ds_classif_evento_w IS NOT NULL AND ds_classif_evento_w::text <> '') then 
	ds_mensagem_w	:= ds_mensagem_w||quebra_w|| 
			wheb_mensagem_pck.get_texto(305650,'DS_DETALHAMENTO='||ds_classif_evento_w);
end if;
 
ds_mensagem_w	:= substr(ds_mensagem_w || quebra_w || quebra_w || wheb_mensagem_pck.get_texto(306012)|| ' '|| ds_evento_w,1,32000);
 
if (ds_perfis_w IS NOT NULL AND ds_perfis_w::text <> '') or (ds_setores_w IS NOT NULL AND ds_setores_w::text <> '') or (ds_usuarios_w IS NOT NULL AND ds_usuarios_w::text <> '') or (ds_grupo_w IS NOT NULL AND ds_grupo_w::text <> '') then 
	insert into 	comunic_interna(  
			dt_comunicado, 
			ds_titulo, 
			ds_comunicado, 
			nm_usuario, 
			dt_atualizacao, 
			ie_geral, 
			nm_usuario_destino, 
			nr_sequencia, 
			ie_gerencial, 
			nr_seq_classif, 
			cd_perfil, 
			cd_setor_destino, 
			cd_estab_destino, 
			ds_setor_adicional, 
			dt_liberacao, 
			ds_perfil_adicional, 
			ds_grupo) 
		values (clock_timestamp(), 
			ds_titulo_w, 
			ds_mensagem_w, 
			nm_usuario_p, 
			clock_timestamp(), 
			'N', 
			ds_usuarios_w, 
			nextval('comunic_interna_seq'), 
			'N', 
			null, 
			null, 
			null, 
			null, 
			ds_setores_w, 
			clock_timestamp(), 
			ds_perfis_w, 
			ds_grupo_w);
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
