SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE enviar_comunic_fim_auxilio (nr_seq_ordem_p text, nm_usuario_p text, cd_estabelecimento_p bigint, nm_destino_p text) AS $body$
DECLARE



ds_texto_w 		varchar(4000);
nr_sequencia_w	bigint;

BEGIN


ds_texto_w :=  obter_desc_expressao(781263)/*'Solicitação realizada na O.S. '*/
||nr_seq_ordem_p||'. '||' Data: '||to_char(clock_timestamp(),'dd/mm/yyyy hh24:mi:ss');

if (nr_seq_ordem_p IS NOT NULL AND nr_seq_ordem_p::text <> '') then

	select	nextval('comunic_interna_seq')
	into STRICT	nr_sequencia_w
	;

	insert	into comunic_interna(cd_estab_destino,
			nr_sequencia,
			ds_titulo,
			dt_comunicado,
			ds_comunicado,
			nm_usuario,
			nm_usuario_destino,
			dt_atualizacao,
			ie_geral,
			ie_gerencial,
			ds_perfil_adicional,
			dt_liberacao)
	values (
			cd_estabelecimento_p,
			nr_sequencia_w,
			obter_desc_expressao(782178)/*'Auxílio O.S.'*/
,
			clock_timestamp(),
			ds_texto_w,
			nm_usuario_p,
			nm_destino_p,
			clock_timestamp(),
			'N',
			'N',
			null,
			clock_timestamp());
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
