SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE enviar_comunic_simpro_bras ( cd_estabelecimento_p bigint, ie_simpro_brasindice_p text, dt_vigencia_p timestamp, cd_versao_p text, nm_usuario_p text) AS $body$
DECLARE

ds_usuarios_w		varchar(255);
ds_perfis_w		varchar(4000);
nm_usuario_w		varchar(15);
cd_perfil_w		bigint;
ds_titulo_w		varchar(255);
vl_parametro_w		varchar(1);
ds_mensagem_w		varchar(255);
ds_usuario_w		varchar(255);
nr_seq_classif_w	bigint;
C01 CURSOR FOR
	SELECT	cd_perfil,
		nm_usuario_envio,
		ds_mensagem
	from	regra_comunic_simp_bras;

BEGIN

vl_parametro_w := Obter_Valor_Param_Usuario(3110, 15, obter_perfil_ativo, nm_usuario_p, 0);

select 	coalesce(max(ds_usuario), nm_usuario_w)
into STRICT	ds_usuario_w
from 	usuario
where 	nm_usuario = nm_usuario_p;

open C01;
loop
fetch C01 into
	cd_perfil_w,
	nm_usuario_w,
	ds_mensagem_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ds_titulo_w:= '';

	if (cd_perfil_w IS NOT NULL AND cd_perfil_w::text <> '') then
		ds_perfis_w:= cd_perfil_w ||',';
	end if;

	if (nm_usuario_w IS NOT NULL AND nm_usuario_w::text <> '') then
		ds_usuarios_w		:= nm_usuario_w||',';
	end if;

	if (coalesce(ds_mensagem_w::text, '') = '')  then

		if (ie_simpro_brasindice_p	= 'S') and (vl_parametro_w = 'U') then
			ds_titulo_w	:= wheb_mensagem_pck.get_Texto(312739, 'DT_VIGENCIA_P='|| to_char(dt_vigencia_p,'dd/mm/yyyy'));/*'Efetuada importação da tabela Simpro com a vigência  '||to_char(dt_vigencia_p,'dd/mm/yyyy');*/
		elsif (ie_simpro_brasindice_p	 = 'B') then
			ds_titulo_w	:= wheb_mensagem_pck.get_texto(312740,'DT_VIGENCIA_P='|| to_char(dt_vigencia_p,'dd/mm/yyyy') ||';CD_VERSAO_P='|| CD_VERSAO_P); /*'Efetuada importação da tabela Brasindice com a vigência  '||to_char(dt_vigencia_p,'dd/mm/yyyy') || ' Versão : '||cd_versao_p;*/
		elsif (ie_simpro_brasindice_p	= 'S') and (vl_parametro_w = 'A') then
			ds_titulo_w	:= wheb_mensagem_pck.get_Texto(312743, 'DT_REF_W='|| to_char(clock_timestamp(),'dd/mm/yyyy')); /*'Efetuada importação da tabela Simpro na data de  '||to_char(sysdate,'dd/mm/yyyy');*/
		end if;
	else
		if (ie_simpro_brasindice_p	= 'S') then
			ds_titulo_w	:= wheb_mensagem_pck.get_texto(312744,'DT_REF_W='|| to_char(clock_timestamp(),'dd/mm/yyyy') ||';DS_MENSAGEM_W='|| DS_MENSAGEM_W ||';DS_USUARIO_W='|| DS_USUARIO_W);
					/*'Caro usuário, foi realizado a atualização do Simpro na data de  ' || to_char(sysdate,'dd/mm/yyyy') || chr(13) || chr(10) || chr(13) || chr(10) ||
					    ds_mensagem_w || chr(13) || chr(10) || chr(13) || chr(10) || 'Atenciosamente ' || chr(13) || chr(10) ||  ds_usuario_w;*/
		elsif (ie_simpro_brasindice_p	 = 'B') then
			ds_titulo_w	:= wheb_mensagem_pck.get_texto(312746,'CD_VERSAO_P='|| CD_VERSAO_P ||';DS_MENSAGEM_W='|| DS_MENSAGEM_W ||';DS_USUARIO_W='|| DS_USUARIO_W);
					/*'Caro usuário, foi realizado a atualização do Brasíndice para a versão ' || cd_versao_p || chr(13) || chr(10) || chr(13) || chr(10) ||
					    ds_mensagem_w || chr(13) || chr(10) || chr(13) || chr(10) || 'Atenciosamente ' || chr(13) || chr(10) || ds_usuario_w;*/
		end if;
	end if;

	if (ds_perfis_w IS NOT NULL AND ds_perfis_w::text <> '') or (ds_usuarios_w IS NOT NULL AND ds_usuarios_w::text <> '') then

		select	max(obter_classif_comunic('F'))
		into STRICT	nr_seq_classif_w
		;

		insert  into 	comunic_interna(
				dt_comunicado,
				ds_titulo,
				ds_comunicado,
				nm_usuario,
				dt_atualizacao,
				ie_geral,
				nm_usuario_destino,
				nr_sequencia,
				ie_gerencial,
				nr_seq_classif,
				cd_perfil,
				cd_setor_destino,
				cd_estab_destino,
				ds_setor_adicional,
				dt_liberacao,
				ds_perfil_adicional)
			values (clock_timestamp(),
				ds_titulo_w,
				ds_titulo_w,
				nm_usuario_p,
				clock_timestamp(),
				'N',
				ds_usuarios_w,
				nextval('comunic_interna_seq'),
				'N',
				nr_seq_classif_w,
				null,
				null,
				null,
				null,
				clock_timestamp(),
				ds_perfis_w);
	end if;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
