SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE enviar_comun_interna_entrega () AS $body$
DECLARE

 
ds_lista_receptores_w		varchar(2000);
nr_transplante_w	varchar(20);
nm_pessoa_fisica_w	varchar(100);
ds_exame_dom_w		varchar(60);
ds_dt_prev_entrega_w	varchar(30);

cd_perfil_solic_exame_w	integer;

c01 CURSOR FOR 
	SELECT 	r.nr_transplante, 
		obter_nome_pf(r.cd_pessoa_fisica) nm_pessoa_fisica, 
		obter_valor_dominio(3349,c.ie_tipo_exame), 
		to_char(c.dt_prev_entrega,'dd/mm/yyyy') 
	from 	tx_controle_exame_comp c, 
		tx_receptor r 
	where (dt_prev_entrega < clock_timestamp()) 
	and (coalesce(dt_entrega::text, '') = '') 
	and	r.nr_sequencia = c.nr_seq_receptor;


BEGIN 
 
ds_lista_receptores_w := wheb_mensagem_pck.get_texto(799381) || ':' || chr(13);
 
open c01;
loop 
fetch c01 into	 
	nr_transplante_w, 
	nm_pessoa_fisica_w, 
	ds_exame_dom_w, 
	ds_dt_prev_entrega_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
	 
	 
	ds_lista_receptores_w := ds_lista_receptores_w || chr(13) || wheb_mensagem_pck.get_texto(799241) || ' :' || nm_pessoa_fisica_w || 
								   '  ' || wheb_mensagem_pck.get_texto(799376) || ' :' || nr_transplante_w || 
								   '  ' || wheb_mensagem_pck.get_texto(798800) || ' :' || ds_exame_dom_w || 
								   '  ' || wheb_mensagem_pck.get_texto(799377) || ' :' || ds_dt_prev_entrega_w;
	 
	end;
end loop;
close c01;
 
 
select	max(cd_perfil_solic_exame) 
into STRICT	cd_perfil_solic_exame_w 
from  tx_parametros;
 
 
INSERT INTO comunic_interna(  
			dt_comunicado, 
			ds_titulo, 
			ds_comunicado, 
			nm_usuario, 
			dt_atualizacao, 
			ie_geral, 
			nm_usuario_destino, 
			nr_sequencia, 
			ie_gerencial, 
			ds_perfil_adicional, 
			dt_liberacao) 
		VALUES (clock_timestamp(), 
			wheb_mensagem_pck.get_texto(799379), 
			ds_lista_receptores_w, 
			'TASY', 
			clock_timestamp(), 
			'N', 
			null, 
			nextval('comunic_interna_seq'), 
			'N', 
			cd_perfil_solic_exame_w, 
			clock_timestamp() 
			);
			 
commit;			
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
